// src/pages/CartPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

export default function CartPage() {
  const { cartItems, removeFromCart, clearCart } = useCart();
  const [orderMessage, setOrderMessage] = useState('');
  const navigate = useNavigate();
  const [fadeIn, setFadeIn] = useState(false);

  useEffect(() => {
    setFadeIn(true); // trigger fade-in
  }, []);

  const handlePlaceOrder = async () => {
    if (!cartItems || cartItems.length === 0) {
      setOrderMessage('Cart is empty or not loaded properly.');
      return;
    }

    const items = cartItems.map((item) => ({
      artwork: item._id,
    }));

    try {
      await axios.post(
        'http://localhost:5000/api/orders/place',
        { items },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        }
      );
      setOrderMessage('âœ… Order placed successfully!');
      clearCart();
      setTimeout(() => navigate('/my-orders'), 1500);
    } catch (err) {
      setOrderMessage(`âŒ Order failed: ${err.response?.data?.message || 'Server error'}`);
    }
  };

  const total = cartItems.reduce((acc, item) => acc + item.price, 0);

  return (
    <div className={`min-h-screen bg-black text-white px-6 py-12 relative overflow-hidden ${fadeIn ? 'animate-fadeIn' : ''}`}>
      {/* Background animated gradient */}
      <div className="absolute inset-0 bg-gradient-to-br from-[#111111] via-[#0e0e0e] to-black z-0 animate-gradient" />

      {/* Floating Orbs */}
      <div className="absolute top-[-100px] left-[60%] w-[300px] h-[300px] bg-fuchsia-600/20 rounded-full blur-3xl animate-float-slow z-0" />
      <div className="absolute bottom-[-100px] right-[60%] w-[350px] h-[350px] bg-cyan-500/20 rounded-full blur-2xl animate-float z-0" />

      <div className="relative z-10 max-w-xl mx-auto">
        <h1 className="text-5xl font-extrabold text-center text-transparent bg-gradient-to-r from-pink-500 via-purple-500 to-indigo-500 bg-clip-text mb-10 drop-shadow-[0_0_15px_rgba(255,255,255,0.1)]">
          ðŸŽ¨ Your Cart
        </h1>

        {orderMessage && (
          <p className="text-center text-red-400 text-lg font-semibold mb-6">{orderMessage}</p>
        )}

        {cartItems.length === 0 ? (
          <div className="text-center text-gray-400 mt-32 text-xl">
            Your cart is looking lonely.
          </div>
        ) : (
          <>
            <div className="space-y-6 max-h-[65vh] overflow-y-auto scrollbar-thin scrollbar-thumb-gray-600 pr-2">
              {cartItems.map((item) => (
                <div
                  key={item._id}
                  className="p-4 backdrop-blur-md bg-white/5 border border-white/10 rounded-2xl shadow-inner shadow-black/40 transition-all duration-300 hover:border-indigo-500 hover:shadow-indigo-500/20"
                >
                  <div className="flex justify-between items-center">
                    <div>
                      <h2 className="text-xl font-semibold text-white">{item.title}</h2>
                      <p className="text-sm text-gray-400 mt-1">â‚¹{item.price}</p>
                    </div>
                    <button
                      onClick={() => removeFromCart(item._id)}
                      className="text-red-500 hover:text-red-400 text-sm tracking-wide"
                    >
                      âœ• Remove
                    </button>
                  </div>
                </div>
              ))}
            </div>

            {/* Summary and Place Order */}
            <div className="mt-8 p-4 rounded-2xl bg-gradient-to-br from-gray-900/60 to-gray-800/40 backdrop-blur-md border border-white/10 shadow-lg">
              <p className="text-lg font-medium text-gray-300 mb-2">
                Total: <span className="text-white font-bold">â‚¹{total}</span>
              </p>
              <button
                onClick={handlePlaceOrder}
                className="w-full mt-3 py-3 rounded-xl text-lg font-semibold bg-gradient-to-r from-purple-600 via-indigo-600 to-blue-600 hover:from-pink-600 hover:to-purple-500 transition-all duration-300 shadow-md shadow-purple-900/30"
              >
                ðŸš€ Place Order
              </button>
            </div>
          </>
        )}
      </div>

      <style>{`
        .scrollbar-thin {
          scrollbar-width: thin;
        }
        .scrollbar-thumb-gray-600 {
          background-color: #4b5563;
        }

        .animate-fadeIn {
          animation: fadeIn 1s ease-out forwards;
        }
        .animate-float {
          animation: float 6s ease-in-out infinite alternate;
        }
        .animate-float-slow {
          animation: float 12s ease-in-out infinite alternate;
        }
        .animate-gradient {
          background-size: 400% 400%;
          animation: gradientShift 15s ease infinite;
        }

        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(20px); }
          to { opacity: 1; transform: translateY(0); }
        }

        @keyframes float {
          0% { transform: translateY(0px) rotate(0deg); }
          100% { transform: translateY(-30px) rotate(360deg); }
        }

        @keyframes gradientShift {
          0% { background-position: 0% 50%; }
          50% { background-position: 100% 50%; }
          100% { background-position: 0% 50%; }
        }
      `}</style>
    </div>
  );
}
