// ./pages/DashboardPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const DashboardPage = () => {
  const [stats, setStats] = useState({
    totalArtworks: 0,
    totalOrders: 0,
    totalArtists: 0,
    totalCustomers: 0,
    totalRevenue: 0,
    pendingOrders: 0,
  });

  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) {
      navigate('/login');
      return;
    }

    const fetchStats = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/dashboard/stats', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setStats(res.data);
      } catch (err) {
        console.error('Error fetching stats:', err);
      }
    };

    fetchStats();
  }, [navigate, token]);

  const cards = [
    { label: 'Total Artworks', count: stats.totalArtworks, icon: 'üñºÔ∏è' },
    { label: 'Total Orders', count: stats.totalOrders, icon: 'üõí' },
    { label: 'Registered Artists', count: stats.totalArtists, icon: 'üë©‚Äçüé®' },
    { label: 'Total Customers', count: stats.totalCustomers, icon: 'üë§' },
    { label: 'Total Revenue', count: `‚Çπ${stats.totalRevenue}`, icon: 'üí∞' },
    { label: 'Pending Orders', count: stats.pendingOrders, icon: 'üì¶' },
  ];

  return (
    <div style={{ padding: '2rem', fontFamily: 'Poppins, sans-serif', color: 'white', background: 'linear-gradient(to right, #141e30, #243b55)', minHeight: '100vh' }}>
      <h2 style={{ textAlign: 'center', marginBottom: '2rem' }}>üìä Dashboard Overview</h2>
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px', justifyContent: 'center' }}>
        {cards.map((card, idx) => (
          <div key={idx} style={{
            background: 'rgba(255,255,255,0.1)',
            borderRadius: '15px',
            padding: '20px',
            width: '250px',
            textAlign: 'center',
            boxShadow: '0 5px 15px rgba(0,0,0,0.3)',
            backdropFilter: 'blur(10px)'
          }}>
            <div style={{ fontSize: '40px' }}>{card.icon}</div>
            <h3 style={{ fontSize: '24px' }}>{card.count}</h3>
            <p style={{ opacity: 0.8 }}>{card.label}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default DashboardPage;
