// src/pages/Upload.js
import { useState } from 'react';
import axios from 'axios';

export default function Upload() {
  const [title, setTitle] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [userId, setUserId] = useState('');
  const [price, setPrice] = useState('');
  const [preview, setPreview] = useState('');

  // Function to upload image to the server
  const handleImageUpload = async () => {
    const formData = new FormData();
    formData.append('image', imageFile);

    const res = await axios.post(
      'http://localhost:5000/api/upload/upload',
      formData,
      {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      }
    );

    return res.data.imageUrl; // Example: /uploads/image.jpg
  };

  // Function to post artwork metadata
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const imageUrl = await handleImageUpload();

      await axios.post('http://localhost:5000/api/upload/image', {
        title,
        image: imageUrl,
        artistId: userId,
        price,
      });

      alert('Artwork uploaded successfully!');
      // Clear form
      setTitle('');
      setImageFile(null);
      setUserId('');
      setPrice('');
      setPreview('');
    } catch (err) {
      console.error(err);
      alert('Upload failed!');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '1rem', padding: '1rem' }}>
      <input placeholder="User ID" value={userId} onChange={(e) => setUserId(e.target.value)} required />
      <input placeholder="Art Title" value={title} onChange={(e) => setTitle(e.target.value)} required />

      <input
        type="file"
        accept="image/*"
        onChange={(e) => {
          setImageFile(e.target.files[0]);
          setPreview(URL.createObjectURL(e.target.files[0]));
        }}
        required
      />
      {preview && <img src={preview} alt="Preview" width="200" />}

      <input
        type="number"
        placeholder="Price (â‚¹)"
        value={price}
        onChange={(e) => setPrice(e.target.value)}
        required
      />
      <button type="submit">Post Artwork</button>
    </form>
  );
}
