// src/pages/Home.js
import React, { useEffect, useState } from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

export default function Home() {
  const [arts, setArts] = useState([]);
  const { addToCart } = useCart();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchArts = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/artworks');
        setArts(res.data);
      } catch (err) {
        console.error('Failed to load artworks', err);
      }
    };
    fetchArts();
  }, []);

  const handleBuyNow = (art) => {
    addToCart(art);
    navigate('/cart');
  };

  return (
    <>
    <div className="max-w-6xl mx-auto px-4 py-8">
    <h1 className="text-3xl font-bold mb-6 text-center">Welcome to Our Art Gallery</h1>

    <Carousel 
      autoPlay 
      infiniteLoop 
      showThumbs={false} 
      showStatus={false}
      className="mb-10"
    >
      {/* üñºÔ∏è Set your own image paths here */}
      <div>
        <img src="/images/art1.jpg" alt="Art 1" className="h-[500px] w-full object-cover rounded" />
        <p className="legend text-lg">Art Title 1 - ‚Çπ999</p>
      </div>
      <div>
        <img src="/images/art2.jpg" alt="Art 2" className="h-[500px] w-full object-cover rounded" />
        <p className="legend text-lg">Art Title 2 - ‚Çπ1499</p>
      </div>
      <div>
        <img src="/images/art3.jpg" alt="Art 3" className="h-[500px] w-full object-cover rounded" />
        <p className="legend text-lg">Art Title 3 - ‚Çπ1999</p>
      </div>
    </Carousel>


      {/* Grid Section (optional) */}
      <h2 className="text-2xl font-semibold mb-4">Explore Artworks</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {arts.map((art) => (
          <div key={art._id} className="border p-4 rounded shadow">
            <img
              src={`http://localhost:5000${art.imageUrl}`}
              alt={art.title}
              className="w-full h-48 object-cover rounded mb-2"
            />
            <h2 className="text-xl font-bold">{art.title}</h2>
            <p className="text-gray-600">‚Çπ{art.price}</p>
            <p className="text-sm">{art.description}</p>
            <button
              onClick={() => handleBuyNow(art)}
              className="mt-2 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
            >
              Buy Now
            </button>
          </div>
        ))}
      </div>
    </div>
    </>
  );
}
