import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axios from 'axios';

export default function ArtDetails() {
  const { id } = useParams();
  const [art, setArt] = useState(null);
  const [buyerId, setBuyerId] = useState('');
  const [isSold, setIsSold] = useState(false);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/artworks/${id}`)
      .then(res => {
        setArt(res.data);
        setIsSold(res.data.sold);
      });
  }, [id]);

  const handleBuy = async () => {
    await axios.patch(`http://localhost:5000/api/artworks/${id}/buy`, { buyerId });
    alert('Purchase successful!');
    setIsSold(true);
  };

  return art ? (
    <div>
      <h2>{art.title}</h2>
      <img src={`http://localhost:5000${art.image}`} width={300} />
      <p>Price: â‚¹{art.price}</p>
      <p>Status: {isSold ? "Sold" : "Available"}</p>
      {!isSold && (
        <>
          <input value={buyerId} onChange={(e) => setBuyerId(e.target.value)} placeholder="Your Buyer ID" />
          <button onClick={handleBuy}>Buy Now</button>
        </>
      )}
    </div>
  ) : <p>Loading...</p>;
}
