import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Home() {
  return (
    <>
      <div className="home">
        {/* Header */}
        <div className="bg-blue-900 text-white py-12">
          <div className="text-center">
            <h1 className="text-4xl font-extrabold mb-8">Welcome to Yuvi's Artistry</h1>

            {/* Overlapping Buttons */}
            <div className="relative h-40 flex justify-center items-center">
              <div className="absolute z-30">
                <Button variant="primary" className="btn-custom">Admin</Button>
              </div>
              <div className="absolute z-20 left-1/2 transform -translate-x-1/2 translate-y-6">
                <Button variant="secondary" className="btn-custom">Artist</Button>
              </div>
              <div className="absolute z-10 left-1/2 transform -translate-x-1/2 translate-y-12">
                <Button variant="success" className="btn-custom">Customer</Button>
              </div>
            </div>
          </div>
        </div>

        {/* Carousel */}
        <div className="max-w-6xl mx-auto px-4 py-8">
          <Carousel
            autoPlay
            infiniteLoop
            showThumbs={false}
            showStatus={false}
          >
            {[1, 2, 3, 4, 5].map((num) => (
              <div key={num}>
                <img src={`/images/art${num}.jpg`} alt={`Art ${num}`} className="rounded-xl shadow-lg" />
                <p className="legend">Art Title {num} - â‚¹{num * 500 + 499}</p>
              </div>
            ))}
          </Carousel>
        </div>
      </div>

      <style>{`
        .btn-custom {
          padding: 14px 28px;
          font-size: 1rem;
          font-weight: bold;
          border-radius: 12px;
          color: white;
          box-shadow: 0 8px 20px rgba(0, 0, 0, 0.25);
          transition: transform 0.3s ease, box-shadow 0.3s ease;
        }
        .btn-custom:hover {
          transform: scale(1.05);
          box-shadow: 0 12px 24px rgba(0, 0, 0, 0.3);
        }

        .carousel .legend {
          background: rgba(0, 0, 0, 0.7);
          font-size: 1.2rem;
          padding: 10px;
          border-radius: 8px;
        }

        @media (max-width: 768px) {
          .btn-custom {
            display: block;
            margin: 10px auto;
            position: static !important;
            transform: none !important;
          }
        }
      `}</style>
    </>
  );
}
