// src/pages/CartPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

export default function CartPage() {
  const { cartItems, removeFromCart, clearCart } = useCart();
  const [orderMessage, setOrderMessage] = useState('');
  const navigate = useNavigate();
  const [fadeIn, setFadeIn] = useState(false);

  useEffect(() => {
    setFadeIn(true);
  }, []);

  const handlePlaceOrder = async () => {
    if (!cartItems || cartItems.length === 0) {
      setOrderMessage('Cart is empty or not loaded properly.');
      return;
    }

    const items = cartItems.map((item) => ({
      artwork: item._id,
    }));

    try {
      await axios.post(
        'http://localhost:5000/api/orders/place',
        { items },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        }
      );
      setOrderMessage('‚úÖ Order placed successfully!');
      clearCart();
      setTimeout(() => navigate('/my-orders'), 1000);
    } catch (err) {
      setOrderMessage(`‚ùå Order failed: ${err.response?.data?.message || 'Server error'}`);
    }
  };

  return (
    <div className={`min-h-screen p-6 bg-black text-white relative overflow-hidden ${fadeIn ? 'fade-in' : ''}`}>
      {/* Particle Background */}
      <div className="particle-bg absolute top-0 left-0 w-full h-full -z-10"></div>

      <h1 className="text-4xl font-extrabold text-center mb-8 glow-text bg-gradient-to-r from-cyan-400 via-blue-500 to-purple-500 text-transparent bg-clip-text animate-gradient">
        üé® Your Art Cart
      </h1>

      {orderMessage && (
        <p className="text-center text-yellow-400 bg-white/10 p-3 rounded-md shadow-xl mb-4 max-w-xl mx-auto backdrop-blur-xl border border-yellow-300/20">
          {orderMessage}
        </p>
      )}

      {cartItems.length === 0 ? (
        <p className="text-center text-gray-400 text-lg mt-10">üñºÔ∏è Your cart is empty.</p>
      ) : (
        <div className="space-y-6 max-w-2xl mx-auto">
          {cartItems.map((item) => (
            <div
              key={item._id}
              className="cart-item-card flex items-center justify-between p-4 rounded-2xl fancy-glass border border-cyan-300/20 transition-all duration-300 hover:shadow-cyan-md group"
            >
              <div className="flex items-center gap-4">
                <img
                  src={`http://localhost:5000${item.imageUrl}`}
                  alt={item.title}
                  className="w-16 h-16 object-cover rounded-xl border border-cyan-400/50 shadow-md group-hover:scale-105 transition-transform"
                />
                <div>
                  <h2 className="font-semibold text-cyan-200 group-hover:text-cyan-100 transition">{item.title}</h2>
                  <p className="text-sm text-gray-300">‚Çπ{item.price}</p>
                </div>
              </div>
              <button
                onClick={() => removeFromCart(item._id)}
                className="text-red-300 bg-red-800/30 px-3 py-1 rounded-full text-sm hover:bg-red-700/40 transition hover:scale-110 hover:shadow-lg"
              >
                ‚úñ Remove
              </button>
            </div>
          ))}

          <div className="total-bar flex justify-between items-center mt-8 p-5 rounded-2xl fancy-glass border border-cyan-500/30">
            <h3 className="text-xl font-bold text-cyan-200 tracking-wide">Total</h3>
            <span className="text-xl font-semibold text-white glow-text">
              ‚Çπ{cartItems.reduce((acc, curr) => acc + parseFloat(curr.price), 0).toFixed(2)}
            </span>
          </div>

          <button
            onClick={handlePlaceOrder}
            className="w-full mt-6 py-3 text-white font-semibold rounded-2xl bg-gradient-to-r from-cyan-500 to-purple-600 hover:from-blue-600 hover:to-pink-500 shadow-xl hover:shadow-2xl transition-all duration-300 hover:scale-105 hover:ring-2 hover:ring-cyan-400"
          >
            üöÄ Place Order
          </button>
        </div>
      )}

      {/* üåü Extra Stylish CSS */}
      <style>{`
        .fade-in {
          animation: fadeIn 1s ease-in;
        }

        .animate-gradient {
          background-size: 200% 200%;
          animation: gradientMove 5s ease infinite;
        }

        .particle-bg {
          background: radial-gradient(circle at 1px 1px, rgba(255, 255, 255, 0.05) 1px, transparent 0),
                      radial-gradient(circle at 4px 4px, rgba(255, 255, 255, 0.03) 1px, transparent 0);
          background-size: 20px 20px;
          opacity: 0.3;
          animation: pulse 6s infinite ease-in-out;
        }

        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(30px); }
          to { opacity: 1; transform: translateY(0); }
        }

        @keyframes gradientMove {
          0% { background-position: 0% 50%; }
          50% { background-position: 100% 50%; }
          100% { background-position: 0% 50%; }
        }

        @keyframes pulse {
          0%, 100% { transform: scale(1); }
          50% { transform: scale(1.03); }
        }

        .glow-text {
          text-shadow: 0 0 6px rgba(0, 255, 255, 0.4), 0 0 10px rgba(0, 255, 255, 0.3);
        }

        .fancy-glass {
          background: rgba(255, 255, 255, 0.05);
          backdrop-filter: blur(18px);
          box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
          transition: all 0.4s ease;
        }

        .fancy-glass:hover {
          background: rgba(255, 255, 255, 0.08);
          border-color: rgba(0, 255, 255, 0.3);
        }

        .group:hover .glow-text {
          color: #fff;
          text-shadow: 0 0 8px cyan, 0 0 14px blue;
        }

        .hover\\:shadow-cyan-md:hover {
          box-shadow: 0 8px 20px rgba(0, 255, 255, 0.3);
        }
      `}</style>
    </div>
  );
}
