import { useState } from 'react';
import axios from 'axios';

export default function Upload() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [preview, setPreview] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleImageUpload = async () => {
    if (!imageFile) {
      throw new Error('No image selected.');
    }

    const formData = new FormData();
    formData.append('image', imageFile);

    const uploadRes = await axios.post(
      'http://localhost:5000/api/upload/upload',
      formData,
      {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      }
    );

    return uploadRes.data.imageUrl;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    const token = localStorage.getItem('token');
    if (!token) {
      setError('You must be logged in to upload artwork.');
      return;
    }

    if (!imageFile) {
      setError('Please select an image.');
      return;
    }

    try {
      setLoading(true);

      const imageUrl = await handleImageUpload();

      await axios.post(
        'http://localhost:5000/api/artworks',
        {
          title,
          description,
          price,
          imageUrl,
        },
        {
          headers: {
            'Content-Type': 'application/json',
            Authorization: `Bearer ${token}`,
          },
        }
      );

      alert('Artwork uploaded successfully!');
      setTitle('');
      setDescription('');
      setPrice('');
      setImageFile(null);
      setPreview('');
    } catch (err) {
      console.error('Artwork upload error:', err.response || err);
      setError(err.response?.data?.message || 'Upload failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-6 border border-gray-300 rounded shadow bg-white">
      <h2 className="text-2xl font-semibold mb-4 text-center">Upload Artwork</h2>

      {error && (
        <div className="bg-red-100 text-red-700 p-3 mb-4 rounded">
          {error}
        </div>
      )}

      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          className="w-full p-2 border rounded"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Title"
          required
        />
        <textarea
          className="w-full p-2 border rounded"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Description"
          required
        />
        <input
          type="number"
          className="w-full p-2 border rounded"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          placeholder="Price"
          required
          min="0"
        />
        <input
          type="file"
          className="w-full"
          accept="image/*"
          onChange={(e) => {
            const file = e.target.files[0];
            if (file) {
              setImageFile(file);
              setPreview(URL.createObjectURL(file));
            }
          }}
          required
        />
        {preview && (
          <img
            src={preview}
            alt="Preview"
            className="mt-2 rounded shadow"
            width="200"
          />
        )}
        <button
          type="submit"
          disabled={loading}
          className="w-full bg-blue-600 text-white py-2 rounded hover:bg-blue-700 disabled:opacity-50"
        >
          {loading ? 'Uploading...' : 'Upload'}
        </button>
      </form>
    </div>
  );
}
