import React, { useEffect, useState } from 'react';
import axios from 'axios';

const OrderHistory = () => {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const fetchOrders = async () => {
      const token = localStorage.getItem('token');
      const res = await axios.get('http://localhost:5000/api/orders/buyer-orders', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setOrders(res.data);
    };

    fetchOrders();
  }, []);

  return (
    <div className="p-4">
      <h2 className="text-2xl font-bold mb-4">Your Orders</h2>
      {orders.map(order => (
        <div key={order._id} className="border p-4 mb-4 rounded-xl shadow">
          <h3 className="font-semibold">Order ID: {order._id}</h3>
          <p>Date: {new Date(order.createdAt).toLocaleDateString()}</p>
          <ul>
            {order.items.map(item => (
              <li key={item.artworkId._id}>
                {item.artworkId.title} - â‚¹{item.artworkId.price}
              </li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
};

export default OrderHistory;
