import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function MyOrders() {
  const [orders, setOrders] = useState([]);
  const [isLoggedIn, setIsLoggedIn] = useState(true);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setIsLoggedIn(false);
      setLoading(false);
      return;
    }

    const fetchOrders = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/orders/my-orders', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setOrders(res.data);
      } catch (err) {
        console.error('Failed to fetch orders:', err);
        setIsLoggedIn(false);
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  if (loading)
    return <div className="text-center p-8 text-cyan-400 text-xl">🌟 Loading your masterpieces...</div>;

  if (!isLoggedIn) {
    return (
      <div className="p-8 text-center">
        <h2 className="text-2xl font-bold text-red-600">🚫 Please login to view your orders.</h2>
      </div>
    );
  }

  if (orders.length === 0) {
    return (
      <div className="p-8 text-center">
        <h2 className="text-2xl font-bold text-gray-300">🖼️ No orders yet. Start collecting art!</h2>
      </div>
    );
  }

  return (
    <div className="p-8 max-w-6xl mx-auto text-white relative z-10">
      <h2 className="text-4xl font-extrabold text-cyan-400 mb-8 drop-shadow-glow">🧾 My Orders</h2>

      <div className="flex flex-col gap-10">
        {orders.map((order) => (
          <div
            key={order._id}
            className="order-card glass-effect p-6 rounded-2xl border border-cyan-200/20 bg-white/10 backdrop-blur-md shadow-md hover:shadow-cyan-400/30 transition-all duration-300"
          >
            <div className="flex flex-col md:flex-row justify-between items-center mb-6 gap-4">
              <p className="text-sm text-gray-300">
                🗓️ Placed on:{' '}
                <span className="text-cyan-300 font-medium">
                  {new Date(order.placedAt || order.createdAt).toLocaleDateString('en-IN', {
                    year: 'numeric',
                    month: 'short',
                    day: 'numeric',
                  })}
                </span>
              </p>
              <button
                onClick={() => handleDelete(order._id)}
                className="px-4 py-2 bg-red-600 text-white rounded-full shadow-md hover:bg-red-700 transition-all duration-300"
              >
                ❌ Delete Order
              </button>
            </div>

            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
              {order.items.map((item, index) => (
                <div
                  key={index}
                  className="art-card-small bg-gray-900/40 rounded-xl overflow-hidden shadow-lg p-4 border border-cyan-100/20 hover:shadow-cyan-500/40 transition-all duration-300 hover:-translate-y-1 backdrop-blur-md"
                >
                  <img
                    src={`http://localhost:5000${item.imageUrl}`}
                    alt={item.title}
                    className="w-full h-40 object-cover rounded-md mb-3 transition-transform duration-300 hover:scale-105"
                  />
                  <h3 className="text-sm font-semibold text-cyan-300 truncate">{item.title}</h3>
                  <p className="text-sm text-gray-200">💰 ₹{Number(item.price).toFixed(2)}</p>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>

      <style>{`
        body {
          font-family: 'Segoe UI', sans-serif;
          background-color: black;
        }

        .drop-shadow-glow {
          text-shadow: 0 0 10px rgba(0, 255, 255, 0.6);
        }

        .glass-effect {
          background: rgba(255, 255, 255, 0.07);
          border-radius: 1.2rem;
          box-shadow: 0 10px 25px rgba(0,0,0,0.15);
          backdrop-filter: blur(12px);
        }
      `}</style>
    </div>
  );

  async function handleDelete(orderId) {
    const token = localStorage.getItem('token');
    if (!token) return;

    if (!window.confirm('Are you sure you want to delete this order?')) return;

    try {
      await axios.delete(`http://localhost:5000/api/orders/${orderId}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      setOrders((prev) => prev.filter((o) => o._id !== orderId));
    } catch (err) {
      console.error('Failed to delete order:', err);
      alert('Could not delete the order.');
    }
  }
}
