// src/pages/CartPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

export default function CartPage() {
  const { cartItems, removeFromCart, clearCart } = useCart();
  const [orderMessage, setOrderMessage] = useState('');
  const navigate = useNavigate();
  const [fadeIn, setFadeIn] = useState(false);

  useEffect(() => {
    setFadeIn(true);
  }, []);

  const handlePlaceOrder = async () => {
    if (!cartItems || cartItems.length === 0) {
      setOrderMessage('Cart is empty or not loaded properly.');
      return;
    }

    const items = cartItems.map((item) => ({
      artwork: item._id,
    }));

    try {
      await axios.post(
        'http://localhost:5000/api/orders/place',
        { items },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        }
      );
      setOrderMessage('✅ Order placed successfully!');
      clearCart();
      setTimeout(() => navigate('/my-orders'), 1000);
    } catch (err) {
      setOrderMessage(`❌ Order failed: ${err.response?.data?.message || 'Server error'}`);
    }
  };

  return (
    <div className={`min-h-screen p-6 bg-black text-white transition-all duration-700 ${fadeIn ? 'opacity-100' : 'opacity-0'}`}>
      <h1 className="text-4xl text-cyan-400 font-extrabold mb-6 text-center drop-shadow-glow">🛒 Your Cart</h1>
      
      {orderMessage && (
        <p className="text-center text-sm mb-6 text-yellow-400 bg-white/10 rounded p-2">{orderMessage}</p>
      )}

      {cartItems.length === 0 ? (
        <p className="text-center text-gray-400 text-lg">🖼️ Your cart is empty.</p>
      ) : (
        <div className="space-y-6 max-w-2xl mx-auto">
          {cartItems.map((item) => (
            <div
              key={item._id}
              className="glass-card flex justify-between items-center p-4 rounded-xl shadow-lg backdrop-blur-md bg-white/5 border border-cyan-300/20 hover:border-cyan-400/60 transition-all duration-300"
            >
              <div>
                <h2 className="text-lg font-semibold text-cyan-200">{item.title}</h2>
                <p className="text-sm text-gray-300">💰 ₹{item.price}</p>
              </div>
              <button
                onClick={() => removeFromCart(item._id)}
                className="text-red-500 hover:text-red-400 text-sm font-medium bg-red-900/20 px-3 py-1 rounded-full transition-all duration-300 hover:scale-105"
              >
                ✖ Remove
              </button>
            </div>
          ))}

          <div className="flex justify-between items-center mt-10 border-t border-cyan-800 pt-4">
            <h3 className="text-xl font-bold text-cyan-300">Total</h3>
            <span className="text-lg text-gray-100">
              ₹
              {cartItems.reduce((acc, curr) => acc + parseFloat(curr.price), 0).toFixed(2)}
            </span>
          </div>

          <button
            onClick={handlePlaceOrder}
            className="w-full mt-4 py-3 bg-gradient-to-r from-cyan-500 to-blue-500 text-white font-semibold rounded-xl hover:scale-105 hover:shadow-xl transition-all duration-300"
          >
            🚀 Place Order
          </button>
        </div>
      )}

      {/* Stylish Custom CSS */}
      <style>{`
        .glass-card {
          background: rgba(255, 255, 255, 0.05);
          border-radius: 1rem;
          border: 1px solid rgba(0, 255, 255, 0.1);
          box-shadow: 0 8px 32px 0 rgba(31, 38, 135, 0.1);
          backdrop-filter: blur(8px);
          -webkit-backdrop-filter: blur(8px);
          transition: all 0.4s ease;
        }

        .glass-card:hover {
          border: 1px solid rgba(0, 255, 255, 0.3);
          transform: translateY(-2px);
        }

        .drop-shadow-glow {
          text-shadow: 0 0 8px rgba(0, 255, 255, 0.6);
        }

        body {
          background-color: black;
          font-family: 'Segoe UI', sans-serif;
        }
      `}</style>
    </div>
  );
}
