import { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

export default function Home() {
  const [arts, setArts] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchArts = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/artworks');
        setArts(res.data);
      } catch (err) {
        console.error('Failed to fetch artworks:', err);
        setError('Failed to load artworks. Please try again later.');
      }
    };

    fetchArts();
  }, []);

  return (
    <div className="max-w-6xl mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">All Artworks</h1>

      {error && (
        <div className="text-red-500 bg-red-100 p-4 rounded mb-6">
          {error}
        </div>
      )}

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {arts.map((art) => (
          <Link
            to={`/art/${art._id}`}
            key={art._id}
            className="border rounded-lg p-4 hover:shadow-lg transition duration-300"
          >
            <img
              src={`http://localhost:5000${art.image || '/default.jpg'}`}
              alt={art.title || 'Artwork'}
              className="w-full h-48 object-cover rounded mb-4"
            />
            <h3 className="text-lg font-semibold">{art.title}</h3>
            <p className="text-gray-600">â‚¹{art.price}</p>
          </Link>
        ))}
      </div>
    </div>
  );
}
