import { Link, useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';

const Navbar = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    setIsLoggedIn(!!token);

    // Optional: Decode token or fetch user role
    const userData = JSON.parse(localStorage.getItem('user'));
    if (userData?.role === 'admin') {
      setIsAdmin(true);
    }
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user'); // remove user info if stored
    setIsLoggedIn(false);
    setIsAdmin(false);
    navigate('/login');
  };

  return (
    <nav className="bg-blue-600 text-white p-4 flex justify-between items-center">
      <Link to="/gallery" className="font-bold text-xl">Art Gallery</Link>
      <div className="space-x-4">
        <Link to="/">Home</Link>
        <Link to="/gallery">Gallery</Link>
        {isLoggedIn && <Link to="/upload">Upload</Link>}
        {isLoggedIn && <Link to="/cart">Cart</Link>}
        {isLoggedIn && <Link to="/my-orders">My Orders</Link>}
        {isAdmin && <Link to="/admin-dashboard">Admin</Link>}
        {!isLoggedIn && <Link to="/login">Login</Link>}
        {!isLoggedIn && <Link to="/signup">Signup</Link>}
        {isLoggedIn && (
          <button
            onClick={handleLogout}
            className="bg-red-500 text-white px-3 py-1 rounded"
          >
            Logout
          </button>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
