import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function MyOrders() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchOrders = async () => {
      const token = localStorage.getItem('token');
      if (!token) return;

      try {
        const res = await axios.get('http://localhost:5000/api/orders/my-orders', {
          headers: {
            Authorization: `Bearer ${token}`, // updated format
          },
        });

        setOrders(res.data);
      } catch (err) {
        console.error('Failed to fetch orders:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchOrders();
  }, []);

  if (loading) {
    return <p className="text-center mt-10">Loading your orders...</p>;
  }

  if (orders.length === 0) {
    return (
      <div className="p-8 text-center">
        <h2 className="text-2xl font-bold">No orders yet.</h2>
      </div>
    );
  }

  return (
    <div className="p-8 max-w-4xl mx-auto">
      <h2 className="text-3xl font-bold mb-6">My Orders</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {orders.map((order) => {
          const art = order.artworkId;
          return (
            <div key={order._id} className="border p-4 rounded shadow">
              {art ? (
                <>
                  <img
                    src={art.imageUrl}
                    alt={art.title}
                    className="w-full h-40 object-cover rounded mb-2"
                  />
                  <h3 className="font-bold">{art.title}</h3>
                  <p>â‚¹{art.price}</p>
                </>
              ) : (
                <div className="text-sm text-red-500">Artwork no longer available</div>
              )}
              <p className="text-sm text-gray-600">
                Purchased on: {new Date(order.createdAt).toLocaleDateString()}
              </p>
            </div>
          );
        })}
      </div>
    </div>
  );
}
