import { Link, useNavigate } from 'react-router-dom';

export default function Navbar() {
  const navigate = useNavigate();
  const token = localStorage.getItem('token');

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <nav className="flex justify-between items-center bg-gray-900 text-white p-4">
      <h2 className="text-xl font-bold">ðŸŽ¨ Artify</h2>
      <div className="flex gap-4 items-center">
        <Link to="/" className="hover:underline">Home</Link>

        {token && <Link to="/upload" className="hover:underline">Upload</Link>}
        {!token && <Link to="/login" className="hover:underline">Login</Link>}
        {!token && <Link to="/signup" className="hover:underline">Signup</Link>}

        {token && (
          <>
            <Link
              to="/cart"
              className="px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
            >
              Cart
            </Link>
            <Link to="/orders" className="hover:underline">My Orders</Link>
            <Link to="/artist-dashboard" className="hover:underline">Dashboard</Link>
            <button
              onClick={handleLogout}
              className="text-red-400 hover:text-red-600"
            >
              Logout
            </button>
          </>
        )}
      </div>
    </nav>
  );
}
