import { useEffect, useState } from 'react';
import axios from 'axios';
import { useCart } from '../context/CartContext'; // Adjust the path as needed

export default function ArtGallery() {
  const [artworks, setArtworks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { addToCart } = useCart();

  useEffect(() => {
    const fetchArtworks = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/artworks');
        setArtworks(res.data);
      } catch (err) {
        console.error('Failed to fetch artworks:', err);
        setError('Could not load artworks.');
      } finally {
        setLoading(false);
      }
    };

    fetchArtworks();
  }, []);

  if (loading) return <p className="text-center mt-4 text-gray-700">Loading artworks...</p>;
  if (error) return <p className="text-center text-red-500">{error}</p>;

  return (
    <div className="max-w-7xl mx-auto grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 p-4">
      {artworks.map((art) => (
        <div
          key={art._id}
          className="border border-gray-200 rounded-lg shadow-md hover:shadow-lg transition duration-300 bg-white overflow-hidden"
        >
          <img
            src={`http://localhost:5000${art.image}`} // assuming art.image is the relative path
            alt={art.title || 'Artwork'}
            className="w-full h-48 object-cover"
          />
          <div className="p-4">
            <h3 className="text-lg font-semibold">{art.title}</h3>
            <p className="text-sm text-gray-600 mt-1 line-clamp-2">{art.description}</p>
            <p className="font-bold mt-2">â‚¹{art.price}</p>
            <p className="text-sm text-gray-500">By: {art.artistId?.name || 'Unknown Artist'}</p>
            <button
              onClick={() => addToCart(art)}
              className="mt-3 w-full py-2 bg-green-600 text-white rounded hover:bg-green-700 transition"
            >
              Buy
            </button>
          </div>
        </div>
      ))}
    </div>
  );
}
