import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import axios from 'axios';

export default function ArtDetails() {
  const { id } = useParams();
  const [art, setArt] = useState(null);
  const [buyerId, setBuyerId] = useState('');
  const [isSold, setIsSold] = useState(false);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/artworks/${id}`)
      .then(res => {
        setArt(res.data);
        setIsSold(res.data.sold);
      });
  }, [id]);

  const handleBuy = async () => {
    try {
      await axios.patch(`http://localhost:5000/api/artworks/${id}/buy`, { buyerId });
      alert("Purchase successful!");
      setIsSold(true);
    } catch {
      alert("Purchase failed!");
    }
  };

  if (!art) return <p>Loading...</p>;

  return (
    <div>
      <img src={`http://localhost:5000${art.image}`} alt={art.title} width="250" />
      <h2>{art.title}</h2>
      <p>Price: â‚¹{art.price}</p>
      <p>Status: {isSold ? "Sold" : "Available"}</p>
      {!isSold && (
        <>
          <input placeholder="Your Buyer ID" value={buyerId} onChange={(e) => setBuyerId(e.target.value)} />
          <button onClick={handleBuy}>Buy This Artwork</button>
        </>
      )}
    </div>
  );
}
