import React, { useEffect, useState } from 'react';
import axios from 'axios';

const MyArtworks = () => {
  const [myArts, setMyArts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchMyArtworks = async () => {
      try {
        const token = localStorage.getItem('token'); // Get JWT from storage
        if (!token) {
          setError('Please login to view your artworks.');
          return;
        }

        const res = await axios.get('http://localhost:5000/api/artworks/my-artworks', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setMyArts(res.data);
      } catch (err) {
        console.error('Error fetching my artworks:', err);
        setError('Failed to load your artworks.');
      } finally {
        setLoading(false);
      }
    };

    fetchMyArtworks();
  }, []);

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <h2 className="text-3xl font-bold mb-6">My Uploaded Artworks</h2>

      {loading && <p className="text-gray-600">Loading...</p>}
      {error && <p className="text-red-500">{error}</p>}

      {!loading && myArts.length === 0 && !error && (
        <p className="text-gray-700">No artworks uploaded yet.</p>
      )}

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {myArts.map((art) => (
          <div key={art._id} className="border p-4 rounded-lg shadow bg-white">
            <img
              src={art.imageUrl}
              alt={art.title}
              className="w-full h-48 object-cover rounded mb-4"
            />
            <h3 className="text-lg font-semibold">{art.title}</h3>
            <p className="text-sm text-gray-600 mb-1">{art.description}</p>
            <p className="font-semibold text-gray-800">â‚¹{art.price}</p>
            <p className={`text-sm mt-1 ${art.sold ? 'text-red-600' : 'text-green-600'}`}>
              Status: {art.sold ? 'Sold' : 'Available'}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default MyArtworks;
