import { useEffect, useState } from 'react';
import axios from 'axios';

export default function Profile() {
  const [myArtworks, setMyArtworks] = useState([]);

  const fetchMyArtworks = async () => {
    const token = localStorage.getItem('token');
    if (!token) return;

    try {
      const res = await axios.get('http://localhost:5000/api/artworks/my-artworks', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMyArtworks(res.data);
    } catch (err) {
      console.error('Failed to fetch your artworks:', err);
    }
  };

  useEffect(() => {
    fetchMyArtworks();
  }, []);

  const handleDelete = async (id) => {
    const confirm = window.confirm('Are you sure you want to delete this artwork?');
    if (!confirm) return;

    const token = localStorage.getItem('token');
    try {
      await axios.delete(`http://localhost:5000/api/artworks/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMyArtworks(myArtworks.filter((art) => art._id !== id));
    } catch (err) {
      console.error('Delete failed:', err);
    }
  };

  return (
    <div className="p-6 max-w-5xl mx-auto">
      <h2 className="text-3xl font-bold mb-4">My Uploaded Artworks</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {myArtworks.map((art) => (
          <div key={art._id} className="border p-4 rounded shadow">
            <img src={art.imageUrl} alt={art.title} className="w-full h-40 object-cover rounded mb-2" />
            <h3 className="font-semibold">{art.title}</h3>
            <p>â‚¹{art.price}</p>
            <button
              onClick={() => handleDelete(art._id)}
              className="mt-2 bg-red-600 text-white px-3 py-1 rounded"
            >
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
