import { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

// Shared Header Component
function Header() {
  return (
    <header className="bg-black text-white py-4 shadow-md text-center">
      <h1 className="text-3xl font-bold text-cyan-400">🎨 Art Gallery</h1>
    </header>
  );
}

// Navbar Component
function Navbar() {
  return (
    <nav className="bg-gray-800 p-4 shadow-md">
      <div className="flex justify-between items-center max-w-6xl mx-auto">
        <Link to="/" className="text-white font-bold text-xl">Home</Link>
        <div className="space-x-4">
          <Link to="/login" className="text-white">Login</Link>
          <Link to="/signup" className="text-white">Signup</Link>
        </div>
      </div>
    </nav>
  );
}

// Login Form Component
export function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', {
        email,
        password,
      });
      localStorage.setItem('token', res.data.token);
      alert('Login successful!');
      navigate('/');
    } catch (err) {
      alert('Login failed: ' + (err.response?.data?.message || 'Unknown error'));
    }
  };

  return (
    <div className="auth-wrapper">
      <style>{formStyles}</style>
      <Header />
      <Navbar />
      <div className="flex justify-center items-center min-h-screen bg-[#121212]">
        <form onSubmit={handleLogin} className="auth-form">
          <h2 className="form-title">🔐 Login</h2>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Email"
            required
            className="form-input"
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            required
            className="form-input"
          />
          <button type="submit" className="form-button login">
            Login
          </button>
          <div className="form-footer">
            <span>Don't have an account? </span>
            <Link to="/signup" className="form-link">Signup</Link>
          </div>
        </form>
      </div>
    </div>
  );
}

// Signup Form Component
export function Signup() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/auth/signup', {
        name,
        email,
        password,
      });
      alert('Signup successful! Now log in.');
      navigate('/login');
    } catch (err) {
      alert('Signup failed: ' + (err.response?.data?.message || 'Unknown error'));
    }
  };

  return (
    <div className="auth-wrapper">
      <style>{formStyles}</style>
      <Header />
      <Navbar />
      <div className="flex justify-center items-center min-h-screen bg-[#121212]">
        <form onSubmit={handleSignup} className="auth-form">
          <h2 className="form-title">📝 Signup</h2>
          <input
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Name"
            required
            className="form-input"
          />
          <input
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Email"
            required
            className="form-input"
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            required
            className="form-input"
          />
          <button type="submit" className="form-button signup">
            Signup
          </button>
          <div className="form-footer">
            <span>Already have an account? </span>
            <Link to="/login" className="form-link">Login</Link>
          </div>
        </form>
      </div>
    </div>
  );
}

// Form Styles
const formStyles = `
body {
  background: linear-gradient(135deg, #1e1e2f, #121212);
  font-family: 'Segoe UI', sans-serif;
}

.auth-wrapper {
  display: flex;
  justify-content: center;
  align-items: center;
  min-height: 100vh;
}

.auth-form {
  background: #1f1f2e;
  padding: 2.5rem;
  border-radius: 15px;
  box-shadow: 0 0 15px rgba(0, 255, 255, 0.1);
  max-width: 400px;
  width: 100%;
}

.form-title {
  text-align: center;
  font-size: 28px;
  margin-bottom: 1.5rem;
  color: #00fff7;
  text-shadow: 0 0 5px #00fff7;
}

.form-input {
  width: 100%;
  padding: 12px 15px;
  margin-bottom: 1rem;
  border: none;
  border-radius: 8px;
  background: #2d2d3f;
  color: #fff;
  font-size: 15px;
  transition: all 0.3s ease;
}

.form-input:focus {
  outline: none;
  box-shadow: 0 0 8px #00ffe0;
  background: #33334d;
}

.form-button {
  width: 100%;
  padding: 12px;
  font-size: 16px;
  font-weight: bold;
  border: none;
  border-radius: 30px;
  cursor: pointer;
  transition: all 0.3s ease;
}

.form-button.login {
  background: #00b894;
  color: white;
}
.form-button.login:hover {
  background: #009879;
}

.form-button.signup {
  background: #0984e3;
  color: white;
}
.form-button.signup:hover {
  background: #0766c4;
}

.form-footer {
  text-align: center;
  margin-top: 1rem;
  font-size: 14px;
  color: #ccc;
}

.form-link {
  color: #00a8ff;
  text-decoration: none;
  font-weight: bold;
}
.form-link:hover {
  text-decoration: underline;
}
`;
