import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Profile() {
  const [user, setUser] = useState(null);
  const [uploadedCount, setUploadedCount] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [editing, setEditing] = useState(false);
  const [selectedRole, setSelectedRole] = useState('');

  useEffect(() => {
    const fetchProfile = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('No token found. Please log in.');
        setLoading(false);
        return;
      }

      try {
        const res = await axios.get('http://localhost:5000/api/auth/profile', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });

        if (!res.data.user) {
          setError('Profile not found.');
        } else {
          setUser(res.data.user);
          setSelectedRole(res.data.user.role);
          setUploadedCount(res.data.uploadedCount || 0);
        }
      } catch (err) {
        console.error('Failed to load profile:', err);
        setError('Failed to load profile. Please log in again.');
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/';
  };

  const handleRoleUpdate = async () => {
    const token = localStorage.getItem('token');
    try {
      const res = await axios.put(
        'http://localhost:5000/api/auth/update-role', // Correct endpoint
        { role: selectedRole },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );
      
      setUser(prev => ({ ...prev, role: selectedRole }));
      setEditing(false);
      if (selectedRole === 'artist') {
        // Refetch artwork count if needed
        const res2 = await axios.get('http://localhost:5000/api/auth/profile', {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setUploadedCount(res2.data.uploadedCount || 0);
      } else {
        setUploadedCount(0);
      }
    } catch (err) {
      console.error('Failed to update role:', err);
      alert('Error updating role.');
    }
  };

  return (
    <div className="profile-container">
      <style>{profileStyles}</style>

      {loading ? (
        <div className="loading">‚è≥ Loading profile...</div>
      ) : error ? (
        <div className="error">{error}</div>
      ) : (
        <>
          <div className="profile-card">
            <h2 className="title">üë§ Profile</h2>
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>Email:</strong> {user.email}</p>

            {editing ? (
              <>
                <label htmlFor="role">Choose Role:</label>
                <select
                  id="role"
                  value={selectedRole}
                  onChange={(e) => setSelectedRole(e.target.value)}
                >
                  <option value="customer">Customer</option>
                  <option value="artist">Artist</option>
                </select>
                <br />
                <button className="edit-btn" onClick={handleRoleUpdate}>‚úÖ Save</button>
              </>
            ) : (
              <>
                <p><strong>Role:</strong> {user.role}</p>
                {user.role === 'artist' && (
                  <p><strong>Artworks Uploaded:</strong> {uploadedCount}</p>
                )}
                <button className="edit-btn" onClick={() => setEditing(true)}>‚úèÔ∏è Edit Role</button>
              </>
            )}
          </div>
          <button onClick={handleLogout} className="logout-btn">
            üîì Logout
          </button>
        </>
      )}
    </div>
  );
}

const profileStyles = `
body {
  background: #000000;
  margin: 0;
  padding: 0;
  font-family: 'Segoe UI', sans-serif;
}

.profile-container {
  max-width: 600px;
  margin: 4rem auto;
  padding: 2rem;
  color: #f5f6fa;
  text-align: center;
  border: 2px solid #00ffff99;
  border-radius: 25px;
}

.profile-container:hover{
  box-shadow: 0 12px 24px rgba(0, 255, 255, 0.25);
}

.profile-card {
  background: #2f3640;
  padding: 2rem;
  border-radius: 15px;
  box-shadow: 0 0 20px rgba(0,255,255,0.1);
}

.title {
  font-size: 28px;
  margin-bottom: 1rem;
  color: #00fff7;
  text-shadow: 0 0 6px #00fff7;
}

.logout-btn, .edit-btn {
  margin-top: 1rem;
  padding: 10px 25px;
  background:rgb(22, 171, 194);
  color: white;
  border: none;
  border-radius: 30px;
  cursor: pointer;
  font-size: 16px;
  transition: background 0.3s ease;
}
.logout-btn:hover {
  background:rgb(194, 71, 22);
}
.edit-btn:hover {
  background:rgb(61, 132, 255);
}

.loading {
  font-size: 20px;
  color: #00a8ff;
}

.error {
  font-size: 18px;
  color: #ff6b81;
  font-weight: bold;
}
`;
