import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function Profile() {
  const [user, setUser] = useState(null);
  const [uploadedCount, setUploadedCount] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({ name: '', email: '', role: '' });

  // Helper to logout and redirect on auth failure
  const logoutAndRedirect = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';  // or '/' depending on your routing
  };

  useEffect(() => {
    const fetchProfile = async () => {
      const token = localStorage.getItem('token');
      if (!token) {
        setError('No token found. Please log in.');
        setLoading(false);
        return;
      }

      try {
        const res = await axios.get('http://localhost:5000/api/auth/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });

        if (!res.data.user) {
          setError('Profile not found.');
        } else {
          const userData = res.data.user;
          setUser(userData);
          setFormData({
            name: userData.name,
            email: userData.email,
            role: userData.role,
          });
          setUploadedCount(res.data.uploadedCount || 0);
        }
      } catch (err) {
        console.error('Failed to load profile:', err);
        if (err.response && err.response.status === 403) {
          setError('Session expired or invalid token. Please log in again.');
          logoutAndRedirect();
        } else {
          setError('Failed to load profile. Please try again later.');
        }
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    window.location.href = '/login';  // Redirect to login on logout
  };

  const handleSave = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('You must be logged in to update profile.');
      return logoutAndRedirect();
    }

    try {
      const res = await axios.put(
        'http://localhost:5000/api/auth/update-profile',
        formData,
        { headers: { Authorization: `Bearer ${token}` } }
      );

      setUser(res.data.user);
      setEditing(false);

      if (formData.role === 'artist') {
        // Refresh uploaded count after role change or update
        const res2 = await axios.get('http://localhost:5000/api/auth/profile', {
          headers: { Authorization: `Bearer ${token}` },
        });
        setUploadedCount(res2.data.uploadedCount || 0);
      } else {
        setUploadedCount(0);
      }
    } catch (err) {
      console.error('Failed to update profile:', err);
      if (err.response && err.response.status === 403) {
        alert('Session expired. Please log in again.');
        logoutAndRedirect();
      } else {
        alert('Error updating profile. Please try again.');
      }
    }
  };

  if (loading) return <div className="loading">⏳ Loading profile...</div>;
  if (error)
    return (
      <div className="profile-container">
        <style>{profileStyles}</style>
        <div className="error">{error}</div>
      </div>
    );
  if (!user) return null;

  return (
    <div className="profile-container">
      <style>{profileStyles}</style>
      <div className="profile-card">
        <h2 className="title">👤 Profile</h2>

        {editing ? (
          <>
            <label>Name:</label>
            <input
              type="text"
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
            /><br />

            <label>Email:</label>
            <input
              type="email"
              value={formData.email}
              onChange={(e) => setFormData({ ...formData, email: e.target.value })}
            /><br />

            <label>Role:</label>
            <select
              value={formData.role}
              onChange={(e) => setFormData({ ...formData, role: e.target.value })}
            >
              <option value="customer">Customer</option>
              <option value="artist">Artist</option>
            </select><br />

            <button className="edit-btn" onClick={handleSave}>✅ Save</button>
          </>
        ) : (
          <>
            <p><strong>Name:</strong> {user.name}</p>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>Role:</strong> {user.role}</p>
            {user.role === 'artist' && (
              <p><strong>Artworks Uploaded:</strong> {uploadedCount}</p>
            )}
            <button className="edit-btn" onClick={() => setEditing(true)}>✏️ Edit Profile</button>
          </>
        )}
      </div>

      <button onClick={handleLogout} className="logout-btn">🔓 Logout</button>
    </div>
  );
}

const profileStyles = `
/* your existing styles here */
`;
