import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const EditArtwork = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [title, setTitle] = useState('');
  const [description, setDesc] = useState('');
  const [price, setPrice] = useState('');
  const [imageUrl, setImageUrl] = useState('');
  const [newImage, setNewImage] = useState(null);
  const [preview, setPreview] = useState('');

  const token = localStorage.getItem('token');

  useEffect(() => {
    const fetchArt = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/artworks/${id}`);
        const { title, description, price, imageUrl } = res.data;
        setTitle(title);
        setDesc(description);
        setPrice(price);
        setImageUrl(imageUrl);
      } catch (err) {
        console.error('Failed to fetch artwork', err);
      }
    };
    fetchArt();
  }, [id]);

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      let updatedImageUrl = imageUrl;

      if (newImage) {
        const formData = new FormData();
        formData.append('image', newImage);

        const imgRes = await axios.post('http://localhost:5000/api/upload/upload', formData);
        updatedImageUrl = imgRes.data.imageUrl;
      }

      await axios.put(`http://localhost:5000/api/artworks/${id}`, {
        title,
        description,
        price,
        imageUrl: updatedImageUrl,
      }, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      alert('Artwork updated!');
      navigate('/my-orders');
    } catch (err) {
      console.error('Update failed', err);
      alert('Update failed');
    }
  };

  return (
    <div className="max-w-md mx-auto mt-10 p-4 border rounded shadow">
      <h2 className="text-2xl font-semibold mb-4">Edit Artwork</h2>
      <form onSubmit={handleUpdate} className="space-y-4">
        <input
          className="w-full p-2 border rounded"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          placeholder="Title"
          required
        />
        <textarea
          className="w-full p-2 border rounded"
          value={description}
          onChange={(e) => setDesc(e.target.value)}
          placeholder="Description"
          required
        />
        <input
          className="w-full p-2 border rounded"
          type="number"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          placeholder="Price"
          required
        />
        <input
          className="w-full"
          type="file"
          accept="image/*"
          onChange={(e) => {
            setNewImage(e.target.files[0]);
            setPreview(URL.createObjectURL(e.target.files[0]));
          }}
        />
        {preview ? (
          <img src={preview} alt="Preview" className="rounded" width="200" />
        ) : (
          <img src={imageUrl} alt="Current" className="rounded" width="200" />
        )}
        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Update
        </button>
      </form>
    </div>
  );
};

export default EditArtwork;
