// src/pages/CartPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

export default function CartPage() {
  const { cartItems, removeFromCart, clearCart } = useCart();
  const [orderMessage, setOrderMessage] = useState('');
  const navigate = useNavigate();
  const [fadeIn, setFadeIn] = useState(false);

  useEffect(() => {
    setFadeIn(true);
  }, []);

  const handlePlaceOrder = async () => {
    if (!cartItems || cartItems.length === 0) {
      setOrderMessage('üõí Your cart is empty.');
      return;
    }

    const items = cartItems.map((item) => ({
      artwork: item._id,
    }));

    try {
      await axios.post(
        'http://localhost:5000/api/orders/place',
        { items },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        }
      );
      setOrderMessage('‚úÖ Order placed successfully!');
      clearCart();
      setTimeout(() => navigate('/my-orders'), 1200);
    } catch (err) {
      setOrderMessage(`‚ùå Order failed: ${err.response?.data?.message || 'Server error'}`);
    }
  };

  return (
    <div className={`min-h-screen p-4 sm:p-6 bg-black text-white relative overflow-hidden ${fadeIn ? 'fade-in' : ''}`}>
      <div className="particle-bg absolute top-0 left-0 w-full h-full -z-10"></div>

      <h1 className="text-3xl sm:text-4xl font-extrabold text-center mb-8 bg-gradient-to-r from-pink-400 via-purple-500 to-cyan-500 text-transparent bg-clip-text animate-gradient">
        üõí Your Art Cart
      </h1>

      {orderMessage && (
        <p className="text-center text-yellow-400 bg-white/10 p-3 rounded-lg shadow-lg mb-6 max-w-md mx-auto">
          {orderMessage}
        </p>
      )}

      {cartItems.length === 0 ? (
        <p className="text-center text-gray-400 text-lg mt-10">No artworks in cart.</p>
      ) : (
        <div className="space-y-5 max-w-md mx-auto w-full">
          {cartItems.map((item) => (
            <div
              key={item._id}
              className="flex items-center justify-between p-3 sm:p-4 rounded-xl backdrop-blur-lg border border-white/10 bg-white/5 hover:bg-white/10 shadow-lg transition group"
            >
              <div className="flex items-center gap-4">
                <img
                  src={item.image || 'https://via.placeholder.com/100x100.png?text=Art'}
                  alt={item.title}
                  className="w-16 h-16 sm:w-20 sm:h-20 object-cover rounded-lg border border-cyan-400/20 shadow-md group-hover:scale-105 transition"
                />
                <div>
                  <h2 className="font-semibold text-pink-200">{item.title}</h2>
                  <p className="text-sm text-gray-300">‚Çπ{item.price}</p>
                </div>
              </div>
              <button
                onClick={() => removeFromCart(item._id)}
                className="text-red-400 text-sm px-3 py-1 border border-red-500/40 rounded-md hover:bg-red-500/20 transition"
              >
                ‚úñ
              </button>
            </div>
          ))}

          <div className="flex justify-between items-center mt-6 p-4 rounded-lg bg-white/10 border border-cyan-300/30 shadow-xl">
            <h3 className="text-lg font-bold text-cyan-300">Total</h3>
            <span className="text-lg font-semibold text-white">
              ‚Çπ{cartItems.reduce((acc, curr) => acc + parseFloat(curr.price), 0).toFixed(2)}
            </span>
          </div>

          <button
            onClick={handlePlaceOrder}
            className="w-full mt-4 py-3 text-white font-bold rounded-xl bg-gradient-to-r from-cyan-400 to-purple-600 hover:from-pink-500 hover:to-yellow-400 shadow-lg hover:shadow-2xl transition-all duration-300 hover:scale-105"
          >
            üöÄ Place Order
          </button>
        </div>
      )}

      {/* Custom Styles */}
      <style>{`
        .fade-in {
          animation: fadeIn 1s ease-in;
        }
        .animate-gradient {
          background-size: 200% 200%;
          animation: gradientMove 5s ease infinite;
        }
        .particle-bg {
          background: radial-gradient(circle at 1px 1px, rgba(255, 255, 255, 0.05) 1px, transparent 0),
                      radial-gradient(circle at 5px 5px, rgba(255, 255, 255, 0.03) 1px, transparent 0);
          background-size: 25px 25px;
          opacity: 0.2;
          animation: pulse 6s infinite ease-in-out;
        }
        @keyframes fadeIn {
          from { opacity: 0; transform: translateY(20px); }
          to { opacity: 1; transform: translateY(0); }
        }
        @keyframes gradientMove {
          0% { background-position: 0% 50%; }
          50% { background-position: 100% 50%; }
          100% { background-position: 0% 50%; }
        }
        @keyframes pulse {
          0%, 100% { transform: scale(1); }
          50% { transform: scale(1.02); }
        }
      `}</style>
    </div>
  );
}
