import React from 'react';
import { useCart } from '../context/CartContext'; // Adjust path
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function Checkout() {
  const { cartItems, clearCart } = useCart();
  const navigate = useNavigate();

  const total = cartItems.reduce((sum, art) => sum + Number(art.price || 0), 0);

  const handlePlaceOrder = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login first!');
      return navigate('/login');
    }

    try {
      // Batch all orders into one API call if your backend supports it
      const response = await axios.post(
        'http://localhost:5000/api/orders',
        {
          items: cartItems.map((art) => ({
            artworkId: art._id,
            price: art.price,
          })),
        },
        {
          headers: {
            Authorization: token,
          },
        }
      );

      if (response.status === 200 || response.status === 201) {
        clearCart();
        alert('Order placed successfully!');
        navigate('/orders');
      } else {
        throw new Error('Unexpected server response');
      }
    } catch (err) {
      console.error('Order error:', err.response || err);
      alert('Failed to place order. Please try again.');
    }
  };

  if (cartItems.length === 0) {
    return (
      <div className="p-8 text-center">
        <h2 className="text-2xl font-bold mb-4">Your cart is empty ðŸ›’</h2>
        <button
          onClick={() => navigate('/gallery')}
          className="text-blue-600 underline"
        >
          Go to Gallery
        </button>
      </div>
    );
  }

  return (
    <div className="p-8 max-w-2xl mx-auto bg-white shadow rounded">
      <h2 className="text-3xl font-bold mb-6">Checkout</h2>
      <ul className="mb-4 space-y-2">
        {cartItems.map((art) => (
          <li key={art._id} className="flex justify-between">
            <span>ðŸŽ¨ {art.title}</span>
            <span>â‚¹{art.price}</span>
          </li>
        ))}
      </ul>
      <h3 className="text-xl font-semibold mb-6">Total: â‚¹{total.toFixed(2)}</h3>
      <button
        onClick={handlePlaceOrder}
        className="px-6 py-2 bg-green-600 text-white rounded hover:bg-green-700"
      >
        Place Order
      </button>
    </div>
  );
}
