import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function Home() {
  return (
    <>
      <div className="home">
        {/* Header */}
        <div className="bg-gradient-to-r from-indigo-900 via-blue-800 to-purple-900 text-white py-14 shadow-2xl">
          <div className="text-center space-y-6 px-4">
            <h1 className="text-5xl sm:text-6xl font-extrabold tracking-wide drop-shadow-lg glowing-text">
              Welcome to Yuvi's Artistry
            </h1>

            <div className="flex justify-center flex-wrap gap-6 mt-6">
              <Button variant="primary" className="btn-custom glass">Admin</Button>
              <Button variant="secondary" className="btn-custom glass">Artist</Button>
              <Button variant="success" className="btn-custom glass">Customer</Button>
            </div>
          </div>
        </div>

        {/* Carousel Section */}
        <div className="max-w-6xl mx-auto px-4 py-12 animate-fade-in-up">
          <Carousel
            autoPlay
            infiniteLoop
            showThumbs={false}
            showStatus={false}
            className="rounded-2xl overflow-hidden shadow-2xl"
          >
            {[
              { src: "/images/art1.jpg", title: "Art Title 1", price: "₹999" },
              { src: "/images/art2.jpg", title: "Art Title 2", price: "₹1499" },
              { src: "/images/art3.jpg", title: "Art Title 3", price: "₹1999" },
              { src: "/images/art4.jpeg", title: "Art Title 4", price: "₹2499" },
              { src: "/images/art5.jpeg", title: "Art Title 5", price: "₹2999" },
            ].map((art, index) => (
              <div key={index}>
                <img
                  src={art.src}
                  alt={art.title}
                  className="w-full object-cover rounded-xl transition-transform duration-700 ease-in-out transform hover:scale-105 hover:brightness-110"
                />
                <p className="legend text-lg">{`${art.title} - ${art.price}`}</p>
              </div>
            ))}
          </Carousel>
        </div>
      </div>

      {/* 🔥 Custom Styles */}
      <style>{`
        .home {
          min-height: 100vh;
          background: linear-gradient(135deg, #0f0c29, #302b63, #24243e);
          animation: gradientMove 20s ease infinite;
        }

        @keyframes gradientMove {
          0% { background-position: 0% 50%; }
          50% { background-position: 100% 50%; }
          100% { background-position: 0% 50%; }
        }

        .btn-custom {
          padding: 12px 28px;
          font-size: 1.15rem;
          font-weight: 600;
          border-radius: 50px;
          text-transform: uppercase;
          font-family: 'Segoe UI', sans-serif;
          color: white;
          border: none;
          transition: all 0.3s ease-in-out;
          background-size: 200% auto;
          box-shadow: 0 0 15px rgba(0,0,0,0.2);
        }

        .btn-custom:hover {
          transform: scale(1.05);
          box-shadow: 0 8px 20px rgba(0,0,0,0.3);
        }

        .btn-primary {
          background-image: linear-gradient(to right, #00b09b, #96c93d);
        }

        .btn-secondary {
          background-image: linear-gradient(to right, #ff512f, #dd2476);
        }

        .btn-success {
          background-image: linear-gradient(to right, #1d976c, #93f9b9);
        }

        .glass {
          backdrop-filter: blur(6px);
          background-color: rgba(255, 255, 255, 0.1) !important;
          border: 1px solid rgba(255, 255, 255, 0.2);
        }

        .legend {
          font-size: 1.5rem;
          font-weight: bold;
          text-align: center;
          background: rgba(0, 0, 0, 0.65);
          color: #fff;
          padding: 12px 24px;
          border-radius: 12px;
          margin-top: 2%;
          font-family: 'Georgia', serif;
          backdrop-filter: blur(4px);
          text-shadow: 1px 1px 4px rgba(0,0,0,0.5);
        }

        .glowing-text {
          text-shadow: 0 0 10px #00ffff, 0 0 20px #00e6e6, 0 0 30px #00cccc;
        }

        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translate3d(0, 30px, 0);
          }
          to {
            opacity: 1;
            transform: translate3d(0, 0, 0);
          }
        }

        .animate-fade-in-up {
          animation: fadeInUp 1s ease-out both;
        }

        /* Responsive */
        @media (max-width: 768px) {
          .legend {
            font-size: 1.2rem;
            padding: 10px;
          }

          .btn-custom {
            font-size: 1rem;
            padding: 10px 20px;
          }

          .glowing-text {
            font-size: 2.5rem;
          }
        }

        @media (max-width: 480px) {
          .legend {
            font-size: 1rem;
            padding: 8px;
          }

          .btn-custom {
            font-size: 0.95rem;
            padding: 8px 16px;
          }

          .glowing-text {
            font-size: 2rem;
          }
        }
      `}</style>
    </>
  );
}
