import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ArtistDashboard = () => {
  const [artworks, setArtworks] = useState([]);
  const [sales, setSales] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const token = localStorage.getItem('token');
      const headers = { Authorization: `Bearer ${token}` };

      const artworkRes = await axios.get('http://localhost:5000/api/artworks/my-artworks', { headers });
      setArtworks(artworkRes.data);

      const salesRes = await axios.get('http://localhost:5000/api/orders/artist-sales', { headers });
      setSales(salesRes.data);
    };

    fetchData();
  }, []);

  const totalEarnings = sales.reduce((sum, order) => {
    order.items.forEach(item => {
      if (item.artistId === localStorage.getItem('userId')) {
        sum += item.artworkId?.price || 0;
      }
    });
    return sum;
  }, 0);

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-6">Artist Dashboard</h2>

      <div className="mb-6">
        <h3 className="text-xl font-semibold">Your Artworks</h3>
        {artworks.map(a => (
          <div key={a._id} className="p-3 border rounded mt-2 shadow">
            <p className="font-bold">{a.title}</p>
            <p>₹{a.price}</p>
          </div>
        ))}
      </div>

      <div>
        <h3 className="text-xl font-semibold mb-2">Your Sales</h3>
        {sales.map(order => (
          <div key={order._id} className="border p-3 rounded mb-3">
            <p><strong>Order ID:</strong> {order._id}</p>
            <ul className="list-disc ml-6">
              {order.items.map((item, index) => (
                item.artistId === localStorage.getItem('userId') && (
                  <li key={index}>
                    {item.artworkId?.title} — ₹{item.artworkId?.price}
                  </li>
                )
              ))}
            </ul>
          </div>
        ))}
        <div className="mt-4 text-lg font-bold">Total Earnings: ₹{totalEarnings}</div>
      </div>
    </div>
  );
};

export default ArtistDashboard;
