import { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

export function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('customer'); // Added role state
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', {
        email,
        password,
        role, // Send role with login request
      });

      const token = res.data.token;
      const receivedRole = res.data.role;

      if (!token || !receivedRole) {
        throw new Error('Login failed: Missing token or role from server response');
      }

      localStorage.setItem('token', token);
      localStorage.setItem('role', receivedRole);

      alert(`Login successful as ${receivedRole}!`);

      // Redirect based on role
      if (receivedRole === 'admin') {
        navigate('/admin/dashboard');
      } else if (receivedRole === 'artist') {
        navigate('/artist/dashboard');
      } else {
        navigate('/customer/home');
      }

    } catch (err) {
      console.error('Login error:', err.response?.data || err.message);
      setError(err.response?.data?.message || 'Login failed: Unknown error');
    }
  };

  return (
    <div className="auth-wrapper">
      <style>{formStyles}</style>
      <form onSubmit={handleLogin} className="auth-form">
        <h2 className="form-title">üîê Login</h2>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
          required
          className="form-input"
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          required
          className="form-input"
        />
        <select
          value={role}
          onChange={(e) => setRole(e.target.value)}
          className="form-input"
        >
          <option value="customer">Customer</option>
          <option value="artist">Artist</option>
          <option value="admin">Admin</option>
        </select>
        {error && <div className="form-error">{error}</div>}
        <button type="submit" className="form-button login">Login</button>
        <div className="form-footer">
          <span>Don't have an account? </span>
          <Link to="/signup" className="form-link">Signup</Link>
        </div>
      </form>
    </div>
  );
}

export function Signup() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('customer');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();
    setError('');
    try {
      await axios.post('http://localhost:5000/api/auth/signup', {
        name,
        email,
        password,
        role,
      });
      alert('Signup successful! Now log in.');
      navigate('/login');
    } catch (err) {
      setError(err.response?.data?.message || 'Signup failed: Unknown error');
    }
  };

  return (
    <div className="auth-wrapper">
      <style>{formStyles}</style>
      <form onSubmit={handleSignup} className="auth-form">
        <h2 className="form-title">üìù Signup</h2>
        <input
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Name"
          required
          className="form-input"
        />
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
          required
          className="form-input"
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          required
          className="form-input"
        />
        <select
          value={role}
          onChange={(e) => setRole(e.target.value)}
          className="form-input"
        >
          <option value="customer">Customer</option>
          <option value="artist">Artist</option>
        </select>
        {error && <div className="form-error">{error}</div>}
        <button type="submit" className="form-button signup">Signup</button>
        <div className="form-footer">
          <span>Already have an account? </span>
          <Link to="/login" className="form-link">Login</Link>
        </div>
      </form>
    </div>
  );
}

const formStyles = `
