  background: rgb(42, 42, 43);
  padding: 3rem;
  border-radius: 20px;
  box-shadow: 0 4px 25px rgba(0, 255, 255, 0.2);
  max-width: 400px;
  width: 100%;
  backdrop-filter: blur(10px);
}

.form-title {
  text-align: center;
  font-size: 30px;
  margin-bottom: 1.5rem;
  color: #2d3436;
  text-shadow: 0 0 10px #00fff7, 0 0 20px #00fff7, 0 0 30px #00fff7;
}

.form-input {
  width: 100%;
  padding: 14px 16px;
  margin-bottom: 1.2rem;
  border: none;
  border-radius: 8px;
  background: #f3f3f5;
  color: #000;
  font-size: 16px;
  transition: all 0.4s ease;
  font-family: 'Times New Roman', serif;
}

.form-input:focus {
  outline: none;
  box-shadow: 0 0 12px #00fff7;
  background: #e0e0e0;
  color: #000;
}

.form-button {
  width: 100%;
  padding: 14px;
  font-size: 17px;
  font-weight: bold;
  border: none;
  border-radius: 35px;
  cursor: pointer;
  transition: all 0.3s ease;
  letter-spacing: 0.5px;
  font-family: 'Times New Roman', serif;
}

.form-button.login {
  background: linear-gradient(145deg, #00fff7, #00fff7);
  color: white;
}

.form-button.login:hover {
  background: #2d3436;
  box-shadow: 0 0 6px #00fff7, 0 0 10px #00fff7;
}

.form-button.signup {
  background: linear-gradient(145deg, #00fff7, #00fff7);
  color: white;
}

.form-button.signup:hover {
  background: #2d3436;
  box-shadow: 0 0 6px #00fff7, 0 0 10px #00fff7;
}

.form-footer {
  text-align: center;
  margin-top: 1.5rem;
  font-size: 15px;
  color: #ccc;
}

.form-link {
  color: #00fff7;
  text-decoration: none;
  font-weight: bold;
  transition: all 0.3s ease;
}

.form-link:hover {
  text-decoration: underline;
  color: #00fff7;
}

.form-error {
  color: #ff6b6b;
  font-size: 14px;
  text-align: center;
  margin-bottom: 1rem;
}
