import React from 'react';
import { useCart } from '../context/CartContext'; // Adjust path
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function Checkout() {
  const { cartItems, clearCart } = useCart();
  const navigate = useNavigate();

  const total = cartItems.reduce((sum, art) => sum + art.price, 0);

  const handlePlaceOrder = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Please login first!');
      return navigate('/login');
    }

    try {
      // Send each artwork as a separate order (optional: batch all in one call)
      for (let art of cartItems) {
        await axios.post(
          'http://localhost:5000/api/orders',
          { artworkId: art._id },
          {
            headers: {
              Authorization: token,
            },
          }
        );
      }

      clearCart();
      alert('Order placed successfully!');
      navigate('/orders');
    } catch (err) {
      console.error('Order error:', err);
      alert('Failed to place order.');
    }
  };

  if (cartItems.length === 0) {
    return (
      <div className="p-8 text-center">
        <h2 className="text-2xl font-bold mb-4">Your cart is empty</h2>
      </div>
    );
  }

  return (
    <div className="p-8 max-w-2xl mx-auto">
      <h2 className="text-3xl font-bold mb-6">Checkout</h2>
      <ul className="mb-4">
        {cartItems.map((art) => (
          <li key={art._id} className="mb-2">
            ðŸŽ¨ <strong>{art.title}</strong> - â‚¹{art.price}
          </li>
        ))}
      </ul>
      <h3 className="text-xl mb-4">Total: â‚¹{total}</h3>
      <button
        onClick={handlePlaceOrder}
        className="px-6 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
      >
        Place Order
      </button>
    </div>
  );
}
