import React, { useEffect, useState } from 'react';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

export default function Home() {
  const [arts, setArts] = useState([]);
  const { addToCart } = useCart();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchArts = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/artworks');
        setArts(res.data);
      } catch (err) {
        console.error('Failed to load artworks', err);
      }
    };
    fetchArts();
  }, []);

  const handleBuyNow = (art) => {
    addToCart(art);
    navigate('/cart');
  };

  return (
    <>
      <div className="max-w-6xl mx-auto px-4 py-8">
        <h1 className="text-3xl font-bold mb-6 text-center text-blue-900">Welcome to Our Art Gallery</h1>

        <Carousel
          autoPlay
          infiniteLoop
          showThumbs={false}
          showStatus={false}
          className="mb-10"
        >
          {/* üñºÔ∏è Custom static slides */}
          <div>
            <img src="/images/art1.jpg" alt="Art 1" className="h-[500px] w-full object-cover rounded-xl shadow-lg" />
            <p className="legend text-lg bg-opacity-70 bg-black text-white p-2">Art Title 1 - ‚Çπ999</p>
          </div>
          <div>
            <img src="/images/art2.jpg" alt="Art 2" className="h-[500px] w-full object-cover rounded-xl shadow-lg" />
            <p className="legend text-lg bg-opacity-70 bg-black text-white p-2">Art Title 2 - ‚Çπ1499</p>
          </div>
          <div>
            <img src="/images/art3.jpg" alt="Art 3" className="h-[500px] w-full object-cover rounded-xl shadow-lg" />
            <p className="legend text-lg bg-opacity-70 bg-black text-white p-2">Art Title 3 - ‚Çπ1999</p>
          </div>
          <div>
            <img src="/images/art4.jpeg" alt="Art 4" className="h-[500px] w-full object-cover rounded-xl shadow-lg" />
            <p className="legend text-lg bg-opacity-70 bg-black text-white p-2">Art Title 4 - ‚Çπ2499</p>
          </div>
          <div>
            <img src="/images/art5.jpeg" alt="Art 5" className="h-[500px] w-full object-cover rounded-xl shadow-lg" />
            <p className="legend text-lg bg-opacity-70 bg-black text-white p-2">Art Title 5 - ‚Çπ2999</p>
          </div>
        </Carousel>

        {/* Grid Section */}
        <h2 className="text-2xl font-semibold mb-6 text-center text-blue-900">Explore Artworks</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {arts.map((art) => (
            <div key={art._id} className="border p-4 rounded-lg shadow-lg transition-all duration-300 ease-in-out transform hover:scale-105 hover:shadow-2xl">
              <img
                src={`http://localhost:5000${art.imageUrl}`}
                alt={art.title}
                className="w-full h-48 object-cover rounded-lg mb-4"
              />
              <h2 className="text-xl font-bold text-blue-900">{art.title}</h2>
              <p className="text-gray-700 text-lg font-medium">‚Çπ{art.price}</p>
              <p className="text-sm text-gray-600 mb-4">{art.description}</p>
              <button
                onClick={() => handleBuyNow(art)}
                className="mt-2 bg-green-600 text-white px-6 py-2 rounded-md hover:bg-green-700 transition-colors"
              >
                Buy Now
              </button>
            </div>
          ))}
        </div>
      </div>

      <style>{`
        .carousel .slide {
          background-color: #f3f4f6;
          width:
        }

        .carousel .legend {
          font-size: 1.25rem;
          font-weight: bold;
          text-align: center;
          background: rgba(0, 0, 0, 0.7);
          color: white;
          padding: 10px;
          border-radius: 5px;
        }

        .carousel .thumb {
          border-radius: 8px;
        }

        .grid img {
          transition: transform 0.3s ease;
        }

        .grid img:hover {
          transform: scale(1.05);
        }

        .button {
          padding: 10px 20px;
          background-color: #38b2ac;
          color: white;
          border-radius: 4px;
          text-align: center;
        }

        .button:hover {
          background-color: #319795;
        }
      `}</style>
    </>
  );
}
