import React, { useEffect, useState } from 'react';
import axios from 'axios';

const MyArtworks = () => {
  const [myArts, setMyArts] = useState([]);

  useEffect(() => {
    const fetchMyArtworks = async () => {
      try {
        const token = localStorage.getItem('token'); // Get JWT from storage
        const res = await axios.get('http://localhost:5000/api/artworks/my-artworks', {
          headers: { Authorization: `Bearer ${token}` }
        });
        setMyArts(res.data);
      } catch (err) {
        console.error('Error fetching my artworks:', err);
      }
    };

    fetchMyArtworks();
  }, []);

  return (
    <div className="my-artworks-container">
      <h2>My Uploaded Artworks</h2>
      <div className="art-grid">
        {myArts.length === 0 ? (
          <p>No artworks uploaded yet.</p>
        ) : (
          myArts.map(art => (
            <div key={art._id} className="art-card">
              <img src={art.imageUrl} alt={art.title} />
              <h3>{art.title}</h3>
              <p>{art.description}</p>
              <p><strong>â‚¹{art.price}</strong></p>
              <p>Status: {art.sold ? 'Sold' : 'Available'}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default MyArtworks;
