import { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

export function Signup() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('user');  // Added state for role
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();
    setError('');
    try {
      await axios.post('http://localhost:5000/api/auth/signup', {
        name,
        email,
        password,
        role,  // Send role to backend
      });
      alert('Signup successful! Now log in.');
      navigate('/login');
    } catch (err) {
      setError(err.response?.data?.message || 'Signup failed: Unknown error');
    }
  };

  return (
    <div className="auth-wrapper">
      <style>{formStyles}</style>
      <form onSubmit={handleSignup} className="auth-form">
        <h2 className="form-title">üìù Signup</h2>
        <input
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Name"
          required
          className="form-input"
        />
        <input
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email"
          required
          className="form-input"
        />
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
          required
          className="form-input"
        />
        <select
          value={role}
          onChange={(e) => setRole(e.target.value)}  // Handle role change
          className="form-input"
        >
          <option value="user">User</option>
          <option value="admin">Admin</option>
        </select>
        {error && <div className="form-error">{error}</div>}
        <button type="submit" className="form-button signup">
          Signup
        </button>
        <div className="form-footer">
          <span>Already have an account? </span>
          <Link to="/login" className="form-link">Login</Link>
        </div>
      </form>
    </div>
  );
}

const formStyles = `
