// src/pages/CartPage.js
import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

export default function CartPage() {
  const { cartItems, removeFromCart, clearCart } = useCart();
  const [orderMessage, setOrderMessage] = useState('');
  const navigate = useNavigate();

  const handlePlaceOrder = async () => {
    if (!cartItems || cartItems.length === 0) {
      setOrderMessage('Cart is empty or not loaded properly.');
      return;
    }

    const items = cartItems.map((item) => ({
      artwork: item._id,
    }));

    try {
      await axios.post(
        'http://localhost:5000/api/orders/place',
        { items },
        {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`,
          },
        }
      );
      setOrderMessage('‚úÖ Order placed successfully!');
      clearCart();
      setTimeout(() => navigate('/my-orders'), 1500);
    } catch (err) {
      setOrderMessage(`‚ùå Order failed: ${err.response?.data?.message || 'Server error'}`);
      console.error('Order failed:', err);
    }
  };

  return (
    <div className="min-h-screen bg-black text-white relative overflow-hidden p-6">
      {/* Starfield animated background */}
      <div className="absolute top-0 left-0 w-full h-full z-0 stars-bg pointer-events-none"></div>

      <div className="relative z-10 max-w-4xl mx-auto fade-in">
        <h1 className="neon-text text-4xl text-center mb-10">üé® Your Art Cart</h1>

        {orderMessage && (
          <p className="text-center mb-6 text-pink-400 text-lg font-semibold">{orderMessage}</p>
        )}

        {cartItems.length === 0 ? (
          <div className="text-center mt-20 text-gray-400">
            <p className="text-xl">üñºÔ∏è Your cart is empty.</p>
            <p className="text-sm mt-2 text-gray-600">Go add some stunning art!</p>
          </div>
        ) : (
          <div className="space-y-6 max-h-[70vh] overflow-y-auto scrollbar-thin scrollbar-thumb-pink-500">
            {cartItems.map((item) => (
              <div
                key={item._id}
                className="cart-item-glass p-4 rounded-xl border border-pink-400/30 backdrop-blur-sm hover:scale-[1.02] transform transition-all duration-300"
              >
                <div className="flex justify-between items-center">
                  <div>
                    <h2 className="text-xl font-bold text-cyan-300">{item.title}</h2>
                    <p className="text-gray-400 text-sm">üí∞ ‚Çπ{item.price}</p>
                  </div>
                  <button
                    onClick={() => removeFromCart(item._id)}
                    className="text-red-400 hover:text-red-600 transition duration-200"
                  >
                    ‚úñ Remove
                  </button>
                </div>
              </div>
            ))}

            <div className="text-center mt-10">
              <button onClick={handlePlaceOrder} className="flicker-button">
                üöÄ Place Order
              </button>
            </div>
          </div>
        )}
      </div>

      <style>{`
        .fade-in {
          animation: fadeIn 0.6s ease-in-out;
        }

        .neon-text {
          font-weight: bold;
          background: linear-gradient(to right, #00ffe1, #ff00ff);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          text-shadow: 0 0 20px #00ffe1;
        }

        .cart-item-glass {
          background: rgba(255, 255, 255, 0.03);
          border: 1px solid rgba(255, 20, 147, 0.3);
          box-shadow: 0 0 12px rgba(255, 20, 147, 0.15);
        }

        .flicker-button {
          padding: 14px 32px;
          font-size: 1.2rem;
          font-weight: 600;
          background: linear-gradient(90deg, #ff00cc, #3333ff);
          color: white;
          border: none;
          border-radius: 50px;
          box-shadow: 0 0 20px #ff00ccaa;
          animation: flicker 1.5s infinite alternate;
          cursor: pointer;
        }

        .flicker-button:hover {
          transform: scale(1.05);
          box-shadow: 0 0 30px #ff00ccdd;
        }

        .stars-bg {
          background: radial-gradient(#ffffff11 1px, transparent 1px);
          background-size: 20px 20px;
          animation: starfield 100s linear infinite;
        }

        @keyframes fadeIn {
          0% { opacity: 0; transform: scale(0.95); }
          100% { opacity: 1; transform: scale(1); }
        }

        @keyframes flicker {
          0%, 100% { opacity: 1; }
          50% { opacity: 0.7; }
        }

        @keyframes starfield {
          0% { background-position: 0 0; }
          100% { background-position: 10000px 10000px; }
        }
      `}</style>
    </div>
  );
}
