import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function ArtistDashboard() {
  const [artworks, setArtworks] = useState([]);
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const fetchDashboardData = async () => {
      const token = localStorage.getItem('token');
      if (!token) return;

      try {
        const [artRes, orderRes] = await Promise.all([
          axios.get('http://localhost:5000/api/artworks/my-artworks', {
            headers: { Authorization: token },
          }),
          axios.get('http://localhost:5000/api/orders/artist-orders', {
            headers: { Authorization: token },
          }),
        ]);

        setArtworks(artRes.data);
        setOrders(orderRes.data);
      } catch (err) {
        console.error('Failed to fetch artist dashboard data:', err);
      }
    };

    fetchDashboardData();
  }, []);

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <h2 className="text-3xl font-bold mb-6">ðŸŽ¨ Artist Dashboard</h2>

      <section className="mb-10">
        <h3 className="text-xl font-semibold mb-3">My Artworks</h3>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {artworks.map((art) => (
            <div key={art._id} className="border p-4 rounded shadow">
              <img src={art.imageUrl} alt={art.title} className="w-full h-40 object-cover rounded mb-2" />
              <h4 className="font-bold">{art.title}</h4>
              <p>â‚¹{art.price}</p>
              <p className="text-sm text-gray-500">{art.description}</p>
            </div>
          ))}
        </div>
      </section>

      <section>
        <h3 className="text-xl font-semibold mb-3">Orders Received</h3>
        {orders.length === 0 ? (
          <p>No orders yet.</p>
        ) : (
          <div className="space-y-4">
            {orders.map((order) => (
              <div key={order._id} className="border p-4 rounded">
                <p><strong>Artwork:</strong> {order.artworkId?.title}</p>
                <p><strong>Buyer:</strong> {order.buyerId?.name}</p>
                <p><strong>Date:</strong> {new Date(order.createdAt).toLocaleDateString()}</p>
                <p><strong>Price:</strong> â‚¹{order.artworkId?.price}</p>
              </div>
            ))}
          </div>
        )}
      </section>
    </div>
  );
}
