import { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function EditArtwork() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [title, setTitle] = useState('');
  const [description, setDesc] = useState('');
  const [price, setPrice] = useState('');

  useEffect(() => {
    const fetchArt = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/artworks/${id}`);
        const { title, description, price } = res.data;
        setTitle(title);
        setDesc(description);
        setPrice(price);
      } catch (err) {
        console.error('Failed to fetch artwork:', err);
      }
    };
    fetchArt();
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    try {
      await axios.put(`http://localhost:5000/api/artworks/${id}`, {
        title,
        description,
        price,
      }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert('Artwork updated successfully!');
      navigate('/profile');
    } catch (err) {
      console.error(err);
      alert('Update failed');
    }
  };

  return (
    <div className="max-w-lg mx-auto p-4">
      <h2 className="text-2xl font-bold mb-4">Edit Artwork</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input className="w-full p-2 border rounded" value={title} onChange={(e) => setTitle(e.target.value)} required />
        <textarea className="w-full p-2 border rounded" value={description} onChange={(e) => setDesc(e.target.value)} required />
        <input className="w-full p-2 border rounded" type="number" value={price} onChange={(e) => setPrice(e.target.value)} required />
        <button className="bg-blue-600 text-white px-4 py-2 rounded">Update</button>
      </form>
    </div>
  );
}
