// ./pages/Forms.js
import { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';

export function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    try {
      const res = await axios.post('http://localhost:5000/api/auth/login', {
        email,
        password,
      });
      localStorage.setItem('token', res.data.token);
      alert('Login successful!');
      navigate('/');
    } catch (err) {
      setError(err.response?.data?.message || 'Login failed');
    }
  };

  return (
    <div className="auth-wrapper">
      <style>{formStyles}</style>
      <form onSubmit={handleLogin} className="auth-form">
        <h2 className="form-title">🔐 Login</h2>
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" required className="form-input" />
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" required className="form-input" />
        {error && <div className="form-error">{error}</div>}
        <button type="submit" className="form-button login">Login</button>
        <div className="form-footer">
          <span>Don't have an account? </span>
          <Link to="/signup" className="form-link">Signup</Link>
        </div>
      </form>
    </div>
  );
}

export function Signup() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('user');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();
    setError('');
    try {
      await axios.post('http://localhost:5000/api/auth/signup', {
        name,
        email,
        password,
        role,
      });
      alert('Signup successful! Please login.');
      navigate('/login');
    } catch (err) {
      setError(err.response?.data?.message || 'Signup failed');
    }
  };

  return (
    <div className="auth-wrapper">
      <style>{formStyles}</style>
      <form onSubmit={handleSignup} className="auth-form">
        <h2 className="form-title">📝 Signup</h2>
        <input value={name} onChange={(e) => setName(e.target.value)} placeholder="Name" required className="form-input" />
        <input value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email" required className="form-input" />
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" required className="form-input" />
        <select value={role} onChange={(e) => setRole(e.target.value)} className="form-input">
          <option value="user">User</option>
          <option value="admin">Admin</option>
        </select>
        {error && <div className="form-error">{error}</div>}
        <button type="submit" className="form-button signup">Signup</button>
        <div className="form-footer">
          <span>Already have an account? </span>
          <Link to="/login" className="form-link">Login</Link>
        </div>
      </form>
    </div>
  );
}

const formStyles = `
  body {
    background: linear-gradient(135deg, #2a2a35, #121212);
    font-family: 'Times New Roman';
    color: #fff;
  }
  .auth-wrapper {
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: 100vh;
  }
  .auth-form {
    background:rgb(42, 42, 43);
    padding: 3rem;
    border-radius: 20px;
    box-shadow: 0 4px 25px rgba(0, 255, 255, 0.2);
    max-width: 400px;
    width: 100%;
    backdrop-filter: blur(10px);
  }
  .form-title {
    text-align: center;
    font-size: 30px;
    margin-bottom: 1.5rem;
    text-shadow: 0 0 10px #00fff7;
  }
  .form-input {
    width: 100%;
    padding: 14px 16px;
    margin-bottom: 1.2rem;
    border: none;
    border-radius: 8px;
    background:rgb(243, 243, 245);
    color: black;
    font-size: 16px;
    font-family: 'Times New Roman';
  }
  .form-button {
    width: 100%;
    padding: 14px;
    font-size: 17px;
    font-weight: bold;
    border: none;
    border-radius: 35px;
    cursor: pointer;
    font-family: 'Times New Roman';
  }
  .form-button.login, .form-button.signup {
    background: linear-gradient(145deg, #00fff7, #00fff7);
    color: white;
  }
  .form-button:hover {
    background: #2d3436;
    box-shadow: 0 0 6px #00fff7;
  }
  .form-footer {
    text-align: center;
    margin-top: 1.5rem;
    font-size: 15px;
    color: #ccc;
  }
  .form-link {
    color: #00fff7;
    text-decoration: none;
    font-weight: bold;
  }
  .form-error {
    color: #ff6b6b;
    font-size: 14px;
    text-align: center;
    margin-bottom: 1rem;
  }
`;
