import { useState, useEffect } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';

export default function EditArtwork() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [preview, setPreview] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchArtwork = async () => {
      const res = await axios.get(`http://localhost:5000/api/artworks/${id}`);
      const { title, description, price, imageUrl } = res.data;
      setTitle(title);
      setDescription(description);
      setPrice(price);
      setPreview(imageUrl);
    };

    fetchArtwork();
  }, [id]);

  const handleImageUpload = async () => {
    if (!imageFile) return preview;
    const formData = new FormData();
    formData.append('image', imageFile);
    const res = await axios.post('http://localhost:5000/api/upload/upload', formData);
    return res.data.imageUrl;
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const imageUrl = await handleImageUpload();
      await axios.put(
        `http://localhost:5000/api/artworks/${id}`,
        { title, description, price, imageUrl },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      alert('Artwork updated successfully!');
      navigate('/artgallery');
    } catch (err) {
      console.error(err);
      alert('Update failed!');
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <div className="upload-wrapper">
        <div className="upload-container">
          <h2 className="upload-header">🛠️ Edit Your Artwork</h2>
          <form onSubmit={handleUpdate} className="upload-form">
            <div className="form-group">
              <label>Title</label>
              <input value={title} onChange={(e) => setTitle(e.target.value)} required />
            </div>

            <div className="form-group">
              <label>Description</label>
              <textarea value={description} onChange={(e) => setDescription(e.target.value)} required />
            </div>

            <div className="form-group">
              <label>Price (₹)</label>
              <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} required />
            </div>

            <div className="form-group">
              <label>Change Image (optional)</label>
              <input
                type="file"
                accept="image/*"
                onChange={(e) => {
                  setImageFile(e.target.files[0]);
                  setPreview(URL.createObjectURL(e.target.files[0]));
                }}
              />
            </div>

            {preview && (
              <div className="image-preview">
                <img src={preview} alt="Preview" />
              </div>
            )}

            <button type="submit" disabled={loading} className="neon-btn">
              {loading ? 'Updating...' : '💾 Update'}
            </button>
          </form>
        </div>
      </div>

     
    </>
  );
}
