import { Link, useNavigate } from 'react-router-dom';
import { useEffect, useState } from 'react';

export default function Navbar() {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) setUser(JSON.parse(storedUser));
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    setUser(null);
    navigate('/login');
  };

  return (
    <nav className="flex justify-between items-center px-8 py-4 bg-gray-900 text-white shadow">
      <Link to="/" className="text-2xl font-bold">ðŸŽ¨ Artify</Link>

      <div className="flex gap-4 items-center text-sm sm:text-base">
        <Link to="/" className="hover:underline">Home</Link>
        <Link to="/gallery" className="hover:underline">Gallery</Link>
        {user?.role === 'artist' && (
          <>
            <Link to="/upload" className="hover:underline">Upload</Link>
            <Link to="/my-artworks" className="hover:underline">My Artworks</Link>
          </>
        )}
        {user && (
          <Link to="/my-orders" className="hover:underline">My Orders</Link>
        )}
        <Link to="/cart" className="px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600">Cart</Link>

        {!user ? (
          <>
            <Link to="/login" className="hover:underline">Login</Link>
            <Link to="/signup" className="hover:underline">Signup</Link>
          </>
        ) : (
          <button
            onClick={handleLogout}
            className="px-3 py-1 bg-red-500 rounded hover:bg-red-600 text-white"
          >
            Logout
          </button>
        )}
      </div>
    </nav>
  );
}
