import React from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

export default function CartPage() {
  const { cartItems, removeFromCart, clearCart } = useCart();
  const navigate = useNavigate();

  const handlePlaceOrder = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('Login to place orders');
      return;
    }

    try {
      await axios.post('http://localhost:5000/api/orders/place', {
        items: cartItems
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      clearCart();
      alert('Order placed successfully!');
      navigate('/my-orders');
    } catch (err) {
      console.error('Order failed:', err);
      alert('Failed to place order');
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">ðŸ›’ Your Cart</h2>
      {cartItems.length === 0 ? (
        <p>No items in cart.</p>
      ) : (
        <>
          <ul className="space-y-4">
            {cartItems.map((art) => (
              <li key={art._id} className="flex justify-between items-center border p-4 rounded">
                <div>
                  <h3 className="font-semibold">{art.title}</h3>
                  <p>â‚¹{art.price}</p>
                </div>
                <button onClick={() => removeFromCart(art._id)} className="bg-red-500 text-white px-3 py-1 rounded">Remove</button>
              </li>
            ))}
          </ul>

          <button onClick={handlePlaceOrder} className="mt-6 bg-green-600 text-white px-5 py-2 rounded">
            Place Order
          </button>
        </>
      )}
    </div>
  );
}
