import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';


export default function Gallery() {
  const [artworks, setArtworks] = useState([]);
  const [search, setSearch] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const navigate = useNavigate(); // For redirecting


  const fetchArtworks = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/artworks', {
        params: { search, minPrice, maxPrice },
      });
      setArtworks(res.data);
    } catch (err) {
      console.error('Error loading artworks:', err);
    }
  };

  useEffect(() => {
    fetchArtworks();
  }, []);

  const handleFilter = (e) => {
    e.preventDefault();
    fetchArtworks();
  };

  const handleBuyNow = (art) => {
    addToCart(art);           // Add the artwork to cart
    navigate('/cart');        // Navigate to cart page
  };


  return (
    <div className="p-6">
      <h1 className="text-3xl font-bold mb-4">üé® Art Gallery</h1>

      <form onSubmit={handleFilter} className="flex flex-wrap gap-2 mb-6">
        <input
          type="text"
          placeholder="Search by title"
          className="border p-2 rounded w-full sm:w-auto"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <input
          type="number"
          placeholder="Min Price"
          className="border p-2 rounded w-full sm:w-auto"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
        />
        <input
          type="number"
          placeholder="Max Price"
          className="border p-2 rounded w-full sm:w-auto"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
        />
        <button
          type="submit"
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Filter
        </button>
      </form>

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {artworks.map((art) => (
          <div
            key={art._id}
            className="border rounded p-3 shadow hover:shadow-lg transition relative bg-white"
          >
              <img
                src={art.imageUrl}
                alt={art.title}
                className="w-full h-48 object-cover rounded"
              />
              <Link to={`/art/${art._id}`}>
              <h2 className="font-semibold text-lg mt-2" onClick={() => handleBuyNow(art)}>{art.title}</h2>
              </Link>
              <p>‚Çπ{art.price}</p>
              <p className="text-sm">{art.description}</p>

            {/* Always show Edit for now ‚Äî can be restricted to owner/admin later */}
            <Link
              to={`/edit/${art._id}`}
              className="absolute top-2 right-2 bg-blue-100 text-blue-700 px-2 py-1 text-xs rounded hover:bg-blue-200"
            >
              ‚úèÔ∏è Edit
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}
