// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

import Navbar from './components/Navbar';
import Upload from './pages/Upload';
import ArtDetails from './pages/ArtDetails';
import Home from './pages/Home';
import Login from './pages/Login';
import Signup from './pages/Signup';
import ArtGallery from './components/ArtGallery'; // adjust path if different
import Cart from './components/Cart';
import Checkout from './components/Checkout';
import OrderHistory from './components/OrderHistory';
import ArtistDashboard from './components/ArtistDashboard';
import MyArtworks from './components/MyArtworks';
import MyOrders from './pages/MyOrders';







export default function App() {
  return (
    <>
    <Router>
      <Navbar />
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/upload" element={<Upload />} />
        <Route path="/art/:id" element={<ArtDetails />} />
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/gallery" element={<ArtGallery />} />
        <Route path="/cart" element={<Cart />} />
        <Route path="/checkout" element={<Checkout />} />
        <Route path="/orders" element={<OrderHistory />} />
        <Route path="/artist-dashboard" element={<ArtistDashboard />} />
        <Route path="/my-artworks" element={<MyArtworks />} />
        <Route path="/my-orders" element={<MyOrders />} />
      </Routes>
    </Router>
    <div>
      <h1 className="text-3xl font-bold text-center my-4">Art Gallery</h1>
      <ArtGallery />
    </div>
    </>
  );
}
