import { useState, useEffect } from 'react';
import axios from 'axios';

export default function AdminDashboard() {
  const [artworks, setArtworks] = useState([]);

  const fetchArtworks = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/artworks');
      setArtworks(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleDelete = async (id) => {
    const token = localStorage.getItem('token');
    if (!window.confirm('Are you sure you want to delete this artwork?')) return;

    try {
      await axios.delete(`http://localhost:5000/api/artworks/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      setArtworks(artworks.filter((art) => art._id !== id));
    } catch (err) {
      alert('Failed to delete artwork');
      console.error(err);
    }
  };

  useEffect(() => {
    fetchArtworks();
  }, []);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Admin Dashboard</h2>
      <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
        {artworks.map((art) => (
          <div key={art._id} className="border p-2 rounded shadow">
            <img src={art.imageUrl} alt={art.title} className="w-full h-48 object-cover rounded" />
            <h3 className="font-semibold text-lg mt-2">{art.title}</h3>
            <p>â‚¹{art.price}</p>
            <button
              onClick={() => handleDelete(art._id)}
              className="bg-red-500 text-white px-3 py-1 rounded mt-2 hover:bg-red-600"
            >
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
