import { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function Upload() {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [preview, setPreview] = useState('');
  const [loading, setLoading] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(true);

  const navigate = useNavigate();

  // ‚úÖ Auth Check Styling
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (!token) {
      setIsLoggedIn(false);
    }
  }, []);

  // ‚úÖ Image Upload Handler Styling
  const handleImageUpload = async () => {
    const formData = new FormData();
    formData.append('image', imageFile);

    const res = await axios.post('http://localhost:5000/api/upload/upload', formData);
    return res.data.imageUrl;
  };

  // ‚úÖ Submit Handler Styling
  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');
    if (!token) {
      alert('You must be logged in.');
      return;
    }

    try {
      setLoading(true);
      const imageUrl = await handleImageUpload();

      await axios.post(
        'http://localhost:5000/api/artworks',
        { title, description, price, imageUrl },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      alert('Artwork uploaded successfully!');
      navigate('/artgallery');
    } catch (err) {
      console.error(err);
      alert('Upload failed!');
    } finally {
      setLoading(false);
    }
  };

  // ‚úÖ Unauthorized Block
  if (!isLoggedIn) {
    return (
      <div className="text-center mt-10 p-6 text-red-600 font-semibold text-lg bg-red-50 border border-red-200 rounded-xl shadow-md animate-pulse">
        Please login to upload your artwork.
      </div>
    );
  }

  // ‚úÖ Main Upload Form Styling
  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-tr from-pink-50 via-white to-indigo-100 px-4">
      <div className="w-full max-w-xl p-10 bg-white/60 border border-gray-200 rounded-3xl shadow-xl backdrop-blur-md">
        <h2 className="text-3xl font-serif font-semibold text-center text-indigo-700 mb-8 tracking-wide">
          Upload Artwork
        </h2>

        <form onSubmit={handleSubmit} className="space-y-6 text-gray-700">

          {/* üé® Title */}
          <div>
            <label className="block text-sm font-semibold mb-1">Title</label>
            <input
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
              placeholder="Enter title"
              className="w-full px-4 py-2 rounded-lg border border-gray-300 bg-white/80 shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-300 transition"
            />
          </div>

          {/* üìù Description */}
          <div>
            <label className="block text-sm font-semibold mb-1">Description</label>
            <textarea
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              required
              placeholder="Short description"
              className="w-full px-4 py-3 rounded-lg border border-gray-300 bg-white/80 shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-300 transition"
            />
          </div>

          {/* üí∞ Price */}
          <div>
            <label className="block text-sm font-semibold mb-1">Price (‚Çπ)</label>
            <input
              type="number"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              required
              placeholder="Enter price"
              className="w-full px-4 py-2 rounded-lg border border-gray-300 bg-white/80 shadow-sm focus:outline-none focus:ring-2 focus:ring-indigo-300 transition"
            />
          </div>

          {/* üìÅ File Upload */}
          <div>
            <label className="block text-sm font-semibold mb-2">Upload Image</label>
            <input
              type="file"
              accept="image/*"
              required
              onChange={(e) => {
                setImageFile(e.target.files[0]);
                setPreview(URL.createObjectURL(e.target.files[0]));
              }}
              className="w-full text-sm file:mr-4 file:py-2 file:px-4 file:rounded-full
                         file:border-0 file:text-sm file:font-semibold
                         file:bg-indigo-600 file:text-white hover:file:bg-indigo-700 transition"
            />
          </div>

          {/* üñºÔ∏è Preview */}
          {preview && (
            <div className="flex justify-center mt-4">
              <img
                src={preview}
                alt="Preview"
                className="rounded-xl max-h-64 border border-indigo-200 shadow-lg"
              />
            </div>
          )}

          {/* üöÄ Upload Button */}
          <button
            type="submit"
            disabled={loading}
            className="w-full py-3 text-lg font-semibold text-white bg-indigo-600 rounded-xl shadow-md hover:bg-indigo-700 transition-all duration-200"
          >
            {loading ? 'Uploading...' : 'Upload Artwork'}
          </button>
        </form>
      </div>
    </div>
  );
}
