import express from 'express';
import cors from 'cors';
import path from 'path';

const app = express();
const PORT = 5000;

// Middleware
app.use(cors());
app.use(express.json());

// Serve static files from 'uploads' folder
app.use('/uploads', express.static(path.join(__dirname, 'uploads')));

// Example artwork route
app.get('/api/artworks', async (req, res) => {
  // Example artwork object with image URL stored as 'uploads/filename.jpg'
  const artworks = [
    {
      _id: '1',
      title: 'Krishna Art',
      description: 'Beautiful art of Lord Krishna',
      price: 1200,
      imageUrl: 'uploads/krishna1.jpg',
    },
    // More artworks...
  ];
  res.json(artworks);
});

app.listen(PORT, () => {
  console.log(`Server running on http://localhost:${PORT}`);
});
