import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ArtGallery = () => {
  const [artworks, setArtworks] = useState([]);

  useEffect(() => {
    const fetchArtworks = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/artworks');
        setArtworks(res.data);
      } catch (err) {
        console.error('Error fetching artworks:', err);
      }
    };
    fetchArtworks();
  }, []);

  const addToCart = (art) => {
    let cart = JSON.parse(localStorage.getItem('cart')) || [];
    cart.push(art);
    localStorage.setItem('cart', JSON.stringify(cart));
    alert(`${art.title} added to cart!`);
  };

  return (
    <div className="grid grid-cols-3 gap-4 p-4">
      {artworks.map((art) => (
        <div key={art._id} className="bg-white shadow p-4 rounded">
          <img src={art.imageUrl} alt={art.title} className="w-full h-40 object-cover rounded" />
          <h2 className="text-lg font-bold">{art.title}</h2>
          <p>{art.description}</p>
          <p className="text-blue-600">â‚¹{art.price}</p>
          <p className="text-sm text-gray-500">By: {art.artistId?.name || 'Unknown'}</p>
          <button onClick={() => addToCart(art)} className="mt-2 px-4 py-2 bg-green-500 text-white rounded">
            Buy
          </button>
        </div>
      ))}
    </div>
  );
};

export default ArtGallery;
