import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

export default function Gallery() {
  const [artworks, setArtworks] = useState([]);
  const [search, setSearch] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');
  const navigate = useNavigate();

  const { addToCart } = useCart();

  const fetchArtworks = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/artworks', {
        params: { search, minPrice, maxPrice },
      });
      setArtworks(res.data);
    } catch (err) {
      console.error('Error loading artworks:', err);
    }
  };

  useEffect(() => {
    fetchArtworks();
  }, []);

  const handleFilter = (e) => {
    e.preventDefault();
    fetchArtworks();
  };

  const handleBuyNow = (art) => {
    addToCart(art);
    navigate('/cart');
  };

  return (
    <div className="gallery-container">
      <style>{GalleryStyles}</style>

      <h1 className="gallery-header">🎨 Art Gallery</h1>

      <form onSubmit={handleFilter} className="filter-form">
        <input
          type="text"
          placeholder="Search by title"
          className="filter-input"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <input
          type="number"
          placeholder="Min Price"
          className="filter-input"
          value={minPrice}
          onChange={(e) => setMinPrice(e.target.value)}
        />
        <input
          type="number"
          placeholder="Max Price"
          className="filter-input"
          value={maxPrice}
          onChange={(e) => setMaxPrice(e.target.value)}
        />
        <button type="submit" className="filter-button">Filter</button>
      </form>

      <div className="artwork-grid">
        {artworks.map((art) => (
          <div key={art._id} className="artwork-card">
            <div className="image-container">
              <img
                src={`http://localhost:5000${art.imageUrl}`}
                alt={art.title}
                className="artwork-image"
              />
            </div>

            <Link to={`/art/${art._id}`}>
              <h2 className="artwork-title">{art.title}</h2>
            </Link>
            <p className="artwork-price">₹{art.price}</p>
            <p className="artwork-description">{art.description}</p>

            <button onClick={() => handleBuyNow(art)} className="buy-button">
              🛒 Buy Now
            </button>

            <Link to={`/edit/${art._id}`} className="edit-button">
              ✏️ Edit
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
}

export const GalleryStyles = `
  body {
    font-family: 'Segoe UI', sans-serif;
    background: linear-gradient(to bottom, #f4f6f8, #e0eafc);
    margin: 0;
    padding: 0;
    color: #333;
  }

  .gallery-container {
    padding: 2rem;
    max-width: 1280px;
    margin: auto;
  }

  .gallery-header {
    font-size: 3rem;
    font-weight: bold;
    color: #222;
    text-align: center;
    margin-bottom: 2rem;
    animation: fadeInDown 1s ease-out;
  }

  @keyframes fadeInDown {
    from {
      opacity: 0;
      transform: translateY(-30px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  .filter-form {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    gap: 10px;
    margin-bottom: 2.5rem;
  }

  .filter-input {
    padding: 12px;
    border: 1px solid #ccc;
    border-radius: 10px;
    font-size: 1rem;
    width: 200px;
    transition: all 0.3s ease;
    box-shadow: inset 0 0 3px rgba(0,0,0,0.1);
  }

  .filter-input:focus {
    outline: none;
    border-color: #ff9800;
    box-shadow: 0 0 8px rgba(255, 152, 0, 0.3);
  }

  .filter-button {
    background-color:rgb(22, 155, 155);
    color: white;
    border: none;
    padding: 12px 24px;
    border-radius: 10px;
    cursor: pointer;
    font-size: 1rem;
    font-weight: bold;
    transition: all 0.3s ease;
    box-shadow: 0 4px 12px rgba(255, 152, 0, 0.4);
  }

  .filter-button:hover {
    background-color: #fb8c00;
    transform: translateY(-2px);
  }

  .artwork-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(280px, 1fr));
    gap: 24px;
  }

  .artwork-card {
    background: rgba(255, 255, 255, 0.85);
    backdrop-filter: blur(10px);
    border-radius: 16px;
    overflow: hidden;
    box-shadow: 0 10px 24px rgba(0,0,0,0.12);
    position: relative;
    display: flex;
    flex-direction: column;
    transition: transform 0.3s ease, box-shadow 0.3s ease;
    animation: fadeInUp 0.5s ease;
  }

  @keyframes fadeInUp {
    from {
      opacity: 0;
      transform: translateY(30px);
    }
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  .image-container {
    overflow: hidden;
    height: 220px;
    position: relative;
  }

  .artwork-image {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: transform 0.3s ease;
  }

  .artwork-card:hover .artwork-image {
    transform: scale(1.05);
  }

  .artwork-title {
    font-size: 1.4rem;
    font-weight: 600;
    margin: 1rem 1rem 0.3rem;
    color: #444;
    transition: color 0.2s;
  }

  .artwork-title:hover {
    color: #ff9800;
  }

  .artwork-price {
    margin: 0 1rem;
    font-size: 1.1rem;
    font-weight: bold;
    color: #4caf50;
  }

  .artwork-description {
    margin: 0.3rem 1rem 1.5rem;
    color: #555;
    font-size: 0.95rem;
    line-height: 1.5;
  }

  .buy-button {
    position: absolute;
    bottom: 15px;
    left: 15px;
    padding: 10px 22px;
    background-color: #4caf50;
    color: white;
    border: none;
    border-radius: 30px;
    font-size: 0.95rem;
    font-weight: 500;
    cursor: pointer;
    transition: background-color 0.3s ease, transform 0.2s ease;
  }

  .buy-button:hover {
    background-color: #388e3c;
    transform: scale(1.05);
  }

  .edit-button {
    position: absolute;
    bottom: 15px;
    right: 15px;
    padding: 8px 16px;
    background-color: #1976d2;
    color: white;
    font-size: 0.85rem;
    border-radius: 30px;
    text-decoration: none;
    transition: background-color 0.3s ease, transform 0.2s ease;
  }

  .edit-button:hover {
    background-color: #0d47a1;
    transform: scale(1.05);
  }

  @media (max-width: 768px) {
    .gallery-header {
      font-size: 2.2rem;
    }

    .filter-form {
      flex-direction: column;
      align-items: center;
    }

    .filter-input {
      width: 90%;
    }

    .filter-button {
      width: 50%;
    }
  }
`;
