import { useState } from 'react';
import axios from 'axios';

export default function Upload() {
  const [title, setTitle] = useState('');
  const [imageFile, setImageFile] = useState(null);
  const [userId, setUserId] = useState('');
  const [price, setPrice] = useState('');
  const [preview, setPreview] = useState('');

  const handleImageUpload = async () => {
    const formData = new FormData();
    formData.append('image', imageFile);

    const res = await axios.post('http://localhost:5000/api/artworks/upload', formData);
    return res.data.imageUrl; // e.g., /uploads/filename.jpg
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const imageUrl = await handleImageUpload();

      await axios.post('http://localhost:5000/api/artworks', {
        title,
        image: imageUrl,
        artistId: userId,
        price,
      });

      alert('Artwork uploaded!');
    } catch (err) {
      console.error(err);
      alert('Upload failed!');
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <input placeholder="User ID" value={userId} onChange={(e) => setUserId(e.target.value)} />
      <input placeholder="Art Title" value={title} onChange={(e) => setTitle(e.target.value)} />

      <input
        type="file"
        accept="image/*"
        onChange={(e) => {
          setImageFile(e.target.files[0]);
          setPreview(URL.createObjectURL(e.target.files[0]));
        }}
      />
      {preview && <img src={preview} alt="preview" width="200" />}

      <input
        type="number"
        placeholder="Price (â‚¹)"
        value={price}
        onChange={(e) => setPrice(e.target.value)}
      />
      <button type="submit">Post Artwork</button>
    </form>
  );
}
