import { useEffect, useState } from 'react';
import axios from 'axios';
import ArtCard from '../components/ArtCard'; // Adjust path if needed

export default function Home() {
  const [arts, setArts] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchArts = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/artworks');
        setArts(res.data);
      } catch (err) {
        console.error('Failed to fetch artworks:', err);
        setError('Failed to load artworks. Please try again later.');
      }
    };

    fetchArts();
  }, []);

  return (
    <div className="max-w-6xl mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">All Artworks</h1>

      {error && (
        <div className="text-red-500 bg-red-100 p-4 rounded mb-6">
          {error}
        </div>
      )}

      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
        {arts.map((art) => (
          <ArtCard key={art._id} art={art} />
        ))}
      </div>
    </div>
  );
}
