import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import { Button, Card } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const navigate = useNavigate();

  const handleNavigate = (role) => {
    if (role === 'Admin') navigate('/admin-dashboard');
    else if (role === 'Artist') navigate('/artist-dashboard');
    else if (role === 'Customer') navigate('/customer-dashboard');
  };

  return (
    <>
      <div className="max-w-7xl mx-auto px-4 py-8 relative">
        <h1 className="text-4xl font-bold mb-6 text-center text-blue-900 drop-shadow">
          🎨 Welcome to Yuvi's Artistry 🎨
        </h1>

        {/* 🌟 Carousel Section */}
        <div className="relative z-10">
          <Carousel
            autoPlay
            infiniteLoop
            showThumbs={false}
            showStatus={false}
            className="rounded-2xl overflow-hidden shadow-xl"
          >
            {[1, 2, 3, 4, 5].map((num) => (
              <div key={num} className="relative">
                <img
                  src={`/images/art${num}.jpg`}
                  alt={`Art ${num}`}
                  className="h-[480px] w-full object-cover opacity-90 hover:opacity-100 transition duration-300"
                />
                <p className="legend text-xl font-semibold bg-opacity-80 bg-black text-white p-3 rounded-b-xl">
                  Art Title {num} - ₹{num * 500 + 499}
                </p>
              </div>
            ))}
          </Carousel>
        </div>

        {/* 🔑 Cards Section */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-[-60px] z-20 relative px-2 md:px-0">
          {[
            {
              role: 'Admin',
              description: 'Manage all users, artworks, and orders across the platform.',
              icon: '🛠️',
              color: 'from-purple-600 to-indigo-700',
            },
            {
              role: 'Artist',
              description: 'Upload your masterpieces and manage your art sales.',
              icon: '🎨',
              color: 'from-pink-600 to-red-500',
            },
            {
              role: 'Customer',
              description: 'Browse, buy, and enjoy beautiful artworks.',
              icon: '🛒',
              color: 'from-green-600 to-teal-600',
            },
          ].map(({ role, description, icon, color }) => (
            <Card
              key={role}
              className={`bg-gradient-to-br ${color} text-white rounded-2xl p-6 text-center shadow-2xl hover:shadow-3xl transform transition-transform hover:-translate-y-2`}
            >
              <div className="text-6xl mb-3 animate-bounce">{icon}</div>
              <h3 className="text-2xl font-bold mb-2">{role}</h3>
              <p className="mb-4">{description}</p>
              <Button
                onClick={() => handleNavigate(role)}
                variant="light"
                className="font-semibold py-2 px-5 rounded-full shadow-md hover:bg-gray-100 transition duration-300"
              >
                Go to {role}
              </Button>
            </Card>
          ))}
        </div>
      </div>

      <style>{`
        .carousel .slide {
          background-color: #f3f4f6;
        }

        .carousel .legend {
          font-size: 1.25rem;
          font-weight: bold;
          text-align: center;
          background: rgba(0, 0, 0, 0.7);
          color: white;
          padding: 10px;
          border-radius: 5px;
          margin-top: 5%;
        }

        h1 {
          text-align: center;
          text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.3);
        }

        .grid img {
          transition: transform 0.3s ease;
          object-fit: cover;
        }

        .grid img:hover {
          transform: scale(1.05);
        }
      `}</style>
    </>
  );
}
